div.script:
if (!('placeholder' in document.createElement('input'))) {
	var el = document.getElementById('q');
	var orig = 'Search this site';
	el.value = orig;

	el.onclick = function() {
		if (this.value==orig) this.value='';
	}

	el.onblur = function() {
		if (this.value=='') this.value=orig;
	}
}

body.script:

window.onload = init;
var topnavButton = document.getElementById('topnav_mobile');
var topnavDisplay = document.getElementById('topnav');
var headerNews1Toggle = document.getElementById('popup_toggle');
var headerNews1Container = document.getElementById('toggle_container');;

function init() {
	topnavButton.onclick = function(){ topnavDisplay.classList.toggle("open"); }
	headerNews1Toggle.onclick = function(){ headerNews1Container.classList.toggle("open"); }
}
