div.script:
$(function(){
	$('.site-alert .alert-wrapper').each(function(){
		var $wrapper=$(this);
		var id=$wrapper.attr("data-id");
		if (!$.cookie('ALERT_' + id)) {
			$wrapper.show();
			$wrapper.find(".alert-close").on("click", function(){
				$.cookie('ALERT_' + id, "hide", { expires: 7 })
				$wrapper.slideUp();
			});
		}
	});
});

$(function() {
	var $list = $('#district-menu .school-container');
	var $schools = $list.find('.school');
	var $tabs = $('#district-menu .school-type-tabs li');
	var $search = $('#district-menu .school-search');
	var $searchbutton = $search.find('.search-button');
	var $searchinput = $search.find('.search-input input');

	if($schools.find('.other').length == 0) {
		$tabs.filter('.other').hide();
	}

	$tabs.click(function(event) {
		event.preventDefault();
		var $this=$(this);
		if($this.hasClass('active')) {
			$this.removeClass('active').attr("aria-selected", "false");
			$schools.addClass('active');
		} else {
			$tabs.removeClass('active').attr("aria-selected", "false");
			$this.addClass('active').attr("aria-selected", "true");
			$schools.removeClass('active');
			$schools.filter($this.attr('data-filter')).addClass('active');
			//console.log($this.attr('data-filter'), $tabs.filter($this.attr('data-filter')));
		}
	});
	
	var cancelSearch = function() {
		$searchinput.val('');
		$schools.addClass('active');
		$search.removeClass('open');
	}
	
	$searchinput.keyup(function(event){
		if(event.keyCode == 27) { // escape
			event.stopPropagation();
			cancelSearch();
			return;
		}
		var valThis = $(this).val().toLowerCase();
		if(valThis == ""){
			$schools.removeClass('active');
		} else {
			$schools.each(function(){
				var $this = $(this);
				var text = $this.find('.title').text().toLowerCase();
				$this.toggleClass('active', (text.indexOf(valThis) >= 0));
			});
		};
	});
	
	$searchbutton.click(function(event) {
		event.preventDefault();
		
		if($search.hasClass('open')) {
			cancelSearch();
		} else {
			$search.addClass('open');
			$searchinput.focus();
			$schools.removeClass('active');
		}
	});
});

hideHeaderMenu = function() {
	$('header .menu-container').removeClass('open');
	$('header .menu-container .menu-toggle').attr("aria-expanded", "false");
	$('body').off('click', clickForHeaderMenu);
};
showHeaderMenu = function() {
	$('header .schools-container').removeClass('open');
	$('header .schools-container .schools-toggle').attr("aria-expanded", "false");
	$('header .menu-container').addClass('open');
	$('header .menu-container .menu-toggle').attr("aria-expanded", "true");
	$('body').on('click', clickForHeaderMenu);
};

clickForHeaderMenu = function(e) {
	var $container = $('header .menu-container');
	var $target = $(e.target);
	
	// check if tablet-sized+ and open and if click target is not in container
	if($(window).width() >= 768 && $container.is('.open') && !$target.is('.dropdown-backdrop') && !$.contains($container[0], e.target)){
		hideHeaderMenu();
	}
};

// toggle menu
$('header .menu-toggle').on('click', function(e) {
	if($('header .menu-container').is('.open')){
		hideHeaderMenu();
	} else {
		showHeaderMenu();
	}
});

// large/desktop dropdowns on hover
$('header .menu-items').on('mouseenter', '>li.dropdown', function(e) {
	var $this = $(this);
	
	if($(window).width() >= 992 && $this.not('.open')){
		$this.find('a.dropdown-toggle').dropdown('toggle').addClass('disabled');
	}
}).on('mouseleave', '>li.dropdown', function(e) {
	var $this = $(this);
	var $toggle = $this.find('a.dropdown-toggle');
	
	$toggle.removeClass('disabled');
	
	if($(window).width() >= 992 && $this.is('.open')){
		$toggle.dropdown('toggle');
	}
});

