/* HEADER SOCIAL */
hideHeaderSchools = function() {
	$('header .schools-container').removeClass('open');
	$('header .schools-container .schools-toggle').attr("aria-expanded", "false");
	$('body').off('click', clickForHeaderSchools);
};
showHeaderSchools = function() {
	$('header .menu-container').removeClass('open');
	$('header .menu-container .menu-toggle').attr("aria-expanded", "false");
	$('header .schools-container').addClass('open');
	$('header .schools-container .schools-toggle').attr("aria-expanded", "true");
	$('body').on('click', clickForHeaderSchools);
};

clickForHeaderSchools = function(e) {
	var $container = $('header .schools-container');
	
	// check if tablet-sized+ and open and if click target is not in container
	if($(window).width() >= 768 && $container.is('.open') && !$.contains($container[0], e.target)){
		hideHeaderSchools();
	}
};

// toggle schools
$('header .schools-toggle').on('click', function(e) {
	if($('header .schools-container').is('.open')){
		hideHeaderSchools();
	} else {
		showHeaderSchools();
	}
});

// large/desktop dropdowns on hover
$('header .schools-container').on('mouseenter', function(e) {
	if($(window).width() >= 992 && $(this).not('.open')){
		showHeaderSchools();
	}
}).on('mouseleave', function(e) {
	if($(window).width() >= 992 && $(this).is('.open')){
		hideHeaderSchools();
	}
});

// close menu and schools if resizing width at tablet or larger
var windowWidth = $(window).width(); 
$(window).resize(function() {
	if($(window).width() == windowWidth){
		return;
	}
	windowWidth = $(window).width();
	if(windowWidth >= 768){
		if($('header .menu-container').is('.open')){
			hideHeaderMenu();
		}
		if($('header .schools-container').is('.open')){
			hideHeaderSchools();
		}
	}
});

