body.script:
jQuery(document).ready(function(){
	var gTabIndex = 1;
	jQuery('a[href], area[href], input:not([disabled]), select:not([disabled]), textarea:not([disabled]), button:not([disabled]), iframe, object, embed, *[tabindex], *[contenteditable]').each(function(){
		jQuery(this).attr('tabindex',gTabIndex);
		gTabIndex++;
	});
	jQuery('.eventtypelayout_calendar').bind('DOMSubtreeModified',function(e){
		var gTabIndex = 1;
		jQuery('a[href], area[href], input:not([disabled]), select:not([disabled]), textarea:not([disabled]), button:not([disabled]), iframe, object, embed, *[tabindex], *[contenteditable], a.ng-binding, a.footer-action-btn').each(function(){
			jQuery(this).attr('tabindex',gTabIndex);
			gTabIndex++;
		});
	});
});
