if (!window.jQuery) { document.write('<\/script>'); }try { $ = jQuery; } catch(ex) { }if(!window.fancybox) { document.write('<\/script>'); }
if(!window.slick) { document.write('<\/script>'); }




	jQuery(document).ready(function()
	{
		jQuery('[target=_ccmspopup]').on('click', function(e)
		{
			e.preventDefault();
			jQuery.fancybox.open({href : jQuery(this).attr('href'), type: 'iframe', padding : 5 });
		});
	});









		$(document).ready(function() {
			/*
			 *  Simple image gallery. Uses default settings
			 */
			$('.fancybox').fancybox();
			/*
			 *  Different effects
			 */

			/*
			 *  Media helper. Group items, disable animations, hide arrows, enable media and button helpers.
			*/
			$('.fancybox-media')
				.attr('rel', 'media-gallery')
				.fancybox({
            					openEffect : 'none',
            					closeEffect : 'none',
            					prevEffect : 'none',
            					nextEffect : 'none',
            					arrows : false,
            					helpers : {
            						media : {},
            						buttons : {}
            					}
            				});
        });

body.script:
(function(d, s, id) {
    var js, fjs = d.getElementsByTagName(s)[0];
    if (d.getElementById(id)) return;
    js = d.createElement(s); js.id = id;
    js.src = "//connect.facebook.net/en_US/sdk.js#xfbml=1&version=v2.0";
    fjs.parentNode.insertBefore(js, fjs);
}(document, 'script', 'facebook-jssdk'));

    $(document).ready(function(){

       $('.banner .ccms-banner-slider').slick('unslick');
       $('.dashboard .ccms-banner-slider').slick('unslick');

      $('.banner .ccms-banner-slider').slick({
        dots: true,
		infinite: true,
		fade: true,
		arrows: false,
		slide: '.ccms-banner',
		autoplay: true,
		autoplaySpeed: 5000,
		speed: 500,
      });
      $('.dashboard .ccms-banner-slider').slick({
        slidesToShow: 3,
        slidesToScroll: 1,
        autoplay: true,
        autoplaySpeed: 4000,
        speed: 500,
        pauseOnHover: true,
        prevArrow: '',
        nextArrow: '',
        responsive: [
            {
              breakpoint: 990,
              settings: {
                swipe: true,
              }
            },
            {
              breakpoint: 780,
              settings: {
                swipe: true,
                slidesToShow: 1
              }
            }
        ]
      });
    });

var homeUrl = "http://www.spalding.k12.ga.us/Students--Parents/Coronavirus-Information-and-Resources/";
function goHome(){
    top.location.href = homeUrl;
}
function languageChange(id){
    var transUrl = 'http://translate.google.com/translate?u=' + homeUrl + '&sl=en&tl=' + id + '&hl=&ie=UTF-8&anno=0';
    //alert(transUrl);
    top.location.href = transUrl;
}
