link.script:
jQuery(document).on('click','a',function(event){
	var title = $(this).attr('title');
	var html = $(this).text();
	var id = $(this).attr('id');
	var href = $(this).attr('href');
	if(!title || $.trim(title) === "" || typeof title === 'undefined'){
		$(this).attr('title',html);
	}
	if(this.hostname != location.hostname 
		&& id != 'leaveLink' 
		&& href.search('javascript:') == -1
		&& href.search('mailto:') == -1
		&& href.search('tel:') == -1){
		event.preventDefault();
		$(this).leaveNotice({
			siteName: 'Stephens County, GA'
		});
		$(this).trigger('click');
	}
});

