		});*/
	}	

	function megaMenu(){
    	//console.log("start mega menu function");
		var count = 0;
		$(".sw-channel-item").each(function(){
			var myText = $(this).find("> a > span").text().toLowerCase();
			//DO NOT BUILD ON HOME/CALENDAR
			if(myText != "home" && myText != "calendar") {
				var $sectionMenu = $(this).find(".sw-channel-dropdown");
				//ONLY IF HAS SECTION DROPDOWN
				if ($sectionMenu.length > 0) {
					$(this).addClass("mega-parent mega"+count);
					$(this).data("myIndex",count);
					$(this).find("> a").addClass("mega-parent-link");
					$(this).append("




");
					$(".mega-sections", this).append($sectionMenu.clone());
                    $(".mega-sections .sw-channel-dropdown > li", this).attr('id', ''); //added by Lyndsay - Case 00539889 - 5-10-17
					count++;
				}
			}
		});
		$("body").bind("click touchstart", function(e){
		    	var container = $(e.target).closest(".mega-parent");

			// IF TARGET IS NOT IN CONTAINER && NOT DESCENDENT OF CONTAINER
		    	if (!container.is(e.target) && container.has(e.target).length === 0) {
		        	$(".mega-menu").hide();
				$(".mega-active").removeClass("mega-active");
		    	} else {
		    		$(".mega-menu").hide();
				$(".mega-active").removeClass("mega-active");
				if(!container.data("loaded")){
					populateMega(container.data("myIndex"));
				};
				container.addClass("mega-active");
				container.find(".mega-menu").show();
		    	}
		});
		//CHANNEL CLICK DISABLE FOR MEGA PARENT
		$(".mega-parent > a").on("click", function(e){
			e.preventDefault();
			var container = $(e.target).closest(".mega-parent");

			$(".mega-menu").hide();
			$(".mega-active").removeClass("mega-active");
			if(!container.data("loaded")){
				populateMega(container.data("myIndex"));
			};
			container.addClass("mega-active");
			container.find(".mega-menu").show();
		});
	}

	function populateMega(index){
    	//console.log("start populate mega function");
		//BUILD LINKS
		var $menu = $(".mega" + index + " .mega-menu"),
		    obj = megaObj[index],
		    links = obj.links,
		    $container = $menu.find(".mega-links");	

		if (links.length > 0) {

			//IF MORE THAN ONE LINK DETERMINE DISPENSARY
			if(links.length > 1){
				var count = 0,
				    firstCol = Math.ceil(links.length / 2),
				    colArray = [firstCol, (links.length - firstCol)];
				    $cols = $("


");
				$.each(colArray, function(i,o){
					for(var j=0;j<o;j++){
						$cols.find(".col"+i).append("" + links[count].title + "");
						count++;
					}
				});
				$container.append($cols.contents());
			} else {
				$container.append("" + links[0].title + "");
			}
			$container.removeClass("hidden");
		}
		//BUILD NEWS
		if (typeof obj.news !== "undefined") {
			var $link = $("" + obj.news.title + "" + obj.news.desc + "");
			if(obj.news.img.indexOf("default-man.jpg") < 0){ 
				$link.prepend("");
			}
			$menu.find(".column.two").append($link);
		}
		//BUILD SECTIONS
		var sectionCt = $menu.find(".sw-channel-dropdown li").length,
		    firstCol = Math.ceil(sectionCt / 2),
		    sectionCols = [firstCol, (sectionCt - firstCol)],
		    $sect = $menu.find(".sw-channel-dropdown"),
		    $container = $menu.find(".mega-sections");
		if(sectionCt > 1){
			var count = 0;
			$.each(sectionCols, function(i,o){
				$col = $("
");
				for(var j=0;j<o;j++){
					var $item = $sect.find("li").eq(count).find("a");
					$col.append($item);
					count++;
				}
				$container.append($col);
			});
		} else {
			$container.append($sect.find("li").eq(0).find("a"));
		}
		//SETS THIS MENU TO ALREADY LOADED
		$(".mega"+index).addClass("mega-loaded").data("loaded",true);
	}

	function parseMegaObject(){
    	//console.log("start parse mega object function");
		//THIS FUNCTION GOES THROUGH AND BUILDS A PROPER OBJECT REMOVING EMPTY ITEMS
		$.each(defaultMegaObj,function(i,obj){
			//LINKS (MUST HAVE BOTH TITLE AND URL)
			var loopObj = { links:[] };
			for(var j=1;j<7;j++){
				var linkTitle = obj["link" + j + "_title"],
				    linkURL = obj["link" + j + "_url"],
				    linkTarget = obj["link" + j + "_target"];
				if (linkTitle != "" && linkURL != ""){
					loopObj.links.push({title:linkTitle,url:linkURL,target:linkTarget});
				}
			};
			//NEWS (MUST HAVE TITLE & DESC TO BE VALID)
			if (obj.news_title != "" && obj.news_desc != "") {
				var newsObj = {
					title : obj.news_title,
					desc : obj.news_desc,
					img : obj.news_img,
					url : obj.news_url
				};
				loopObj.news = newsObj;
			}
			//PUSH LOOP OBJECT TO MEGA OBJECT
			megaObj.push(loopObj);
		});

	}

	function calendarLinks(){
		var linkArr = [
			["Board of Trustees","https://www.westada.org/domain/79"],	
			["School Supply Lists","https://www.westada.org/SupplyLists"],	
			["District Calendars","https://www.westada.org/Page/14105"]
		];
		var parsedLinks = [];
		$.each(linkArr, function(i,arr){
			if (arr[0] != "" && arr[1] != "") {
				parsedLinks.push({title:arr[0],url:arr[1]});	
			}
		});
		if(parsedLinks.length > 0){
			var $cont = $("
");
			$.each(parsedLinks, function(i,o){
				$cont.append("" + o.title + "");
			});
			($cont).insertAfter($(".hp .view-calendar-link"));
		}
	}

