function init_ad() {
        var ps_in_content,
        ad_div,
        p,
        added = false,
        wc = 0,
        ad_word_margin = ad_nr * word_margin;
    if (mp_global !== undefined && ['article', 'review', 'roundup'].indexOf(mp_global.type) > -1) {
        ps_in_content = document.querySelectorAll('#content > div > p');
        ad_div = document.querySelector("[id='" + div_id + "']");

        [].some.call(ps_in_content, function (p) {
            wc += p.innerHTML.split(' ').length;
            if (wc >= ad_word_margin) {
                p.parentNode.insertBefore(ad_div, p.nextElementSibling);
                googletag.display(div_id);
                added = true;
                return true;
            }
        });

        if (added === false) {
             // the Article is too short so we add the ad at the end
            p = ps_in_content[ps_in_content.length-1];
            p.parentNode.appendChild(ad_div);
            googletag.display(div_id);
        }
    }

}

if (win_width < breakpoint) {
    if (document.readyState != 'loading') {
        setTimeout( init_ad, 1);
    }
    document.addEventListener("DOMContentLoaded", init_ad, false);
