var addthis_config = addthis_config || {};
addthis_config.services_exclude = 'email';

// The tabindex defaults to 1 - which FORCES the tab order to start with the AddThis buttons usually at the bottom of an article page.
// Why do they even add a tabindex attributes to  tags which receive keyboard focus naturally?
// Setting the tabindex for these  tags to zero will cause them to receive focus in the "tabbing flow" of the document (https://davidwalsh.name/tabindex-focus).
//
// https://www.addthis.com/academy/the-addthis_config-variable/
//   ui_tabindex = Sets the global value of the tabindex for all AddThis buttons on the page.
addthis_config.ui_tabindex = 0;

;(function($) { 'use strict';
  $(function() {

    // Wait until all images inside of the article have loaded before getting the height of the container
    $('#articleBody').imagesLoaded(function() {
      var $headlines = $('.headlines'), $em = $headlines.find('.headline-toggle em');
      var artHeight = 0, hdHeight = 0;

      $headlines.find('.headline-toggle a').on('click', function(e) {
        e.preventDefault();

        if ($em.hasClass('fa-chevron-down')) {
          $headlines.height(hdHeight + 20).find('.headline-toggle').height(0);
          $em.addClass('fa-chevron-up').removeClass('fa-chevron-down');
        } else {
          $headlines.height(artHeight).find('.headline-toggle').css('height', '');
          $em.addClass('fa-chevron-down').removeClass('fa-chevron-up');
        }

        $(this).blur();
      });

      // Watch "md" and "lg" breakpoints
      var md = window.matchMedia('(min-width: 992px) and (max-width: 1199px)');
      var lg = window.matchMedia('(min-width: 1200px)');
      md.addListener(widthChange);
      lg.addListener(widthChange);
      if (md.matches) widthChange();
      if (lg.matches) widthChange();

      function widthChange() {

        // Remove any inline height rule before getting the TRUE height
        $headlines.css('height', '');

        artHeight = $('#articleBody').outerHeight(), hdHeight = $('.headlines').outerHeight();

        // Reset the toggle element
        $headlines.find('.headline-toggle').removeClass('hidden');
        if ($em.hasClass('fa-chevron-up')) {
          $headlines.find('.headline-toggle').css('height', '');
          $em.addClass('fa-chevron-down').removeClass('fa-chevron-up');
        }

        // If either breakpoint matches...
        if (md.matches || lg.matches) {

          if (artHeight >= hdHeight) {
            // If the article height is taller than the headlines widget, hide the healines toggle element
            $headlines.find('.headline-toggle').addClass('hidden');
          } else {
            // Change the height of the headlines widget to match the article height and setup the event handler for the toggle
            $headlines.height(artHeight);
          }
        }
      }

    });

  });
})(jQuery);

;(function($) { 'use strict';
  $(function() {

    // Are there any image galleries displayed as thumbnail grids?
    if ($('.ig-thumbnails').length) {
      $('.ig-thumbnails').imagesLoaded(function() {
        $('.ig-thumbnails').each(function() {
          var $this = $(this), height = 0;

          // Get the height of the tallest image in the gallery
          $this.find('img').each(function() {
            if ($(this).height() > height) height = $(this).height();
          });

          // Use that to set the height and max-height of the image contaner and image, respectively.
          $this.find('.thumbnail-inner').css('height', height).end().find('img').css('max-height', height);

          // Get the combined height of credits and captions
          height = 0;
          $this.find('.ig-show-captions-content').each(function() {
            var $this = $(this), x = 0;
            if ($this.find('.ig-credit').length) x = $this.find('.ig-credit').outerHeight();
            if ($this.find('.ig-caption').length) x += $this.find('.ig-caption').outerHeight();
            if (x > height) height = x;
          });

          // Cap the height at 100px
          if (height > 100) height = 100;

          if (height == 0) {
            // If there are no captions/credits - hide the checkbox
            $this.find('> .checkbox').addClass('hide');
          } else {
            // Set the height when the checkbox is clicked (checkbox acts as a toggle on the height).
            $this.find('.ig-show-captions').on('click', function() {
              var $this = $(this);
              if ($this.is(':checked')) {
                $this.closest('.ig-thumbnails').find('.ig-show-captions-content').css('height', height + 4);
              } else {
                $this.closest('.ig-thumbnails').find('.ig-show-captions-content').css('height', 0);
              }
            });
          }
        });
      });
    }

  });
})(jQuery);

