$(document).ready(function(){

 var events_json = "https://api.cps.edu/Calendar/CPS/EventsList?isKeyEvent=Y";

 $.getJSON(events_json, function(data){
     var dis = "";
     $.each(data, function(i, value){
       if(i < 4) {
         dis += '
';
         dis += '
';
         dis += '';
         dis += '
';
         dis += '
';
         dis += '
';
         // dis += '
'+ value.eventStartDateDayOfWeekFullName +'';
         var eventstartdate = moment(value.eventStartDate, "YYYY-MM-DD HH:mm:ss");
         var eventenddate = moment(value.eventEndDate, "YYYY-MM-DD HH:mm:ss");
         dis += '
'+   eventstartdate.format('dddd') +'';
         dis += '';
         dis += '
';
         dis += '
' + eventstartdate.format('MMM') + ' ' + eventstartdate.format('D') +'';
         dis += '';
         dis += '';
         dis += '';
         dis += '';
         dis += '
';
         dis += '
';
         dis += '' + value.eventTitle +'';
         dis += '
' + eventstartdate.format('hh:mm A') + '-' +  eventenddate.format('hh:mm A') + '';
         dis += '';
         dis += '';
         dis += '';
         dis += '';

         dis += '
';
         dis += '
';
         dis += '
';
         dis += '
';
         dis += '';
         dis += '×';
         dis += '';
         dis += '
' + value.eventTitle + '';
         dis += '';
         dis += '
';
         dis += '
When:';

         // dis += '
' + value.eventStartDateDayOfWeekFullName + ', '
         //              + value.eventStartDateMonthFullName + ' '
         //              + value.eventStartDateDayNumeric + ', '
         //              + value.eventStartDateYearNumeric + ' '
         //              + value.eventStartDateStartTime + ' to '
         //              + value.eventEndDateEndTime +
         //        '';
         dis += '
' + eventstartdate.format('dddd, MMMM Do YYYY, h:mm A') + ' to '
           + eventenddate.format('dddd, MMMM Do YYYY, h:mm A') +
           '';
         dis += '
Where:';
         dis += '
' + ((value.eventLocationTitle===null || value.eventLocationTitle==="")?"":value.eventLocationTitle) + '';
         dis += '
' + value.eventLocation + '';
         dis += '
Description:';
         dis += '
