jQuery(function(){
	if(jQuery('#header-language-switcher').length > 0)
		LanguageSwitcher.init();
	else if(LanguageSwitcher.getCookie("language") != undefined && LanguageSwitcher.getCookie("language") == 'es'){
		jQuery('body').addClass('display-language-es');
	}
});

body.script:
   jQuery(function() {
   	   var jq_admin_bar = jQuery('#wpadminbar');
       var jq_alert = jQuery('#alert');
       var jq_lang_switcher = jQuery('#header-language-switcher');
       var jq_header = jQuery('#header');
       var jq_side_menu = jQuery('#side-menu');
       var jq_top_menu = jQuery('#top-menu');
       var jq_html = jQuery('html');

	    setInterval(function() {
	    	var lang_pos = 0;
	    	var header_pos = 0;
	    	if(jq_admin_bar.length > 0){
	    		lang_pos += parseInt(jq_admin_bar.outerHeight());
	    		header_pos += parseInt(jq_admin_bar.outerHeight());
	    	}
	    	if(jq_alert.length > 0){
	    		jq_alert.css({top: header_pos});//offset admin bar
	    		lang_pos += parseInt(jq_alert.outerHeight());
	    		header_pos += parseInt(jq_alert.outerHeight());
	    	}
	    	if(jq_lang_switcher.length > 0){
	    		jq_lang_switcher.css({top: lang_pos});
	    		header_pos += parseInt(jq_lang_switcher.outerHeight());
	    	}
            jq_header.css({top: header_pos});
            jq_side_menu.css({top: header_pos});
            //jq_html.css({'margin-top': header_pos + ' !important'});
            jq_html.attr('style', 'margin-top: '+header_pos+'px !important');
            jq_top_menu.css({top: header_pos + parseInt(jq_header.height())});
	    }, 250);
    });

div.script:
language_change_url = true;
setTimeout(function() {
jQuery('body').on('language_change', function(ev, opts) {
    if (language_change_url) {
        if (opts.language == 'es') {
            location.search = '?es';
        } else {
            location.search = '?';
        }
    }
});
}, 1000);

body.script:
(function() {
  var config = {
    kitId: 'atc8iph'
  };
  var d = false;
  var tk = document.createElement('script');
  tk.src = '//use.typekit.net/' + config.kitId + '.js';
  tk.type = 'text/javascript';
  tk.async = 'true';
  tk.onload = tk.onreadystatechange = function() {
    var rs = this.readyState;
    if (d || rs && rs != 'complete' && rs != 'loaded') return;
    d = true;
    try { Typekit.load(config); } catch (e) {}
  };
  var s = document.getElementsByTagName('script')[0];
  s.parentNode.insertBefore(tk, s);
})();
