        "Search": function() {
            // FOR SCOPE
            var _this = this;

            $("#gb-search-input").focus(function() {
                if($(this).val() == "I’m searching for...") {
                    $(this).val("");
                }
            }).blur(function() {
                if($(this).val() == "") {
                    $(this).val("I’m searching for...");
                }
            });

            // FORM SUMBIT
            $("#gb-search-form").submit(function(e) {
            	e.preventDefault();
                window.location.href = "/site/Default.aspx?PageType=6&SiteID=1&SearchString=" + $("#gb-search-input").val();
            });

            //Show/hide search
            $(document).on("click keydown", ".cs-mystart-item.search", function(e) {            
                if(_this.AllyClick(e)) {
                	e.preventDefault();
                    if($(this).attr("aria-expanded") == "false") {
                    	$(this).attr("aria-expanded", "true");
                        $("#gb-search-outer").attr("aria-hidden", "false").slideDown(250);

                        setTimeout(function() {
                            $("#gb-search-input").focus();
                        }, 260);
                    } else {
                    	$(this).attr("aria-expanded", "false");
                        $("#gb-search-outer").attr("aria-hidden", "true").slideUp(250);
                    }
                }
            });

            $(document).on("keydown", "#gb-search-input", function(e) {
                // CAPTURE KEY CODE
                switch(e.keyCode) {
                    // CONSUME TAB KEY
                    case _this.KeyCodes.tab:
                        e.preventDefault();
                        $(".cs-mystart-item.search").click().focus();
                    break;
                }
            });
        },

        "CalendarListView": function() {
            if($(".ui-widget.app.calendar").length) {
                // AUTO CLICK FUNCTION FOR SCHOOLWIRES CALENDAR LIST VIEW FOR SMALL MOBILE DEVICES
                if(!$(".ui-widget.app.calendar #calendarlist-pnl-specialview.loaded").length){
                    $("a.ui-btn-toggle.list").click();
                    $("a.ui-btn-toggle.month").click();
                    $(".ui-widget.app.calendar #calendarlist-pnl-specialview").addClass("loaded");
                }

                switch(this.GetBreakPoint()) {
                    case "desktop": case "768": case "640":
                        $(".ui-widget.app.calendar #calendar").removeClass("ui-helper-hidden");
                        $(".ui-widget.app.calendar #calendarlist-pnl-specialview").addClass("ui-helper-hidden");
                    break;
                    case "480": case "320":
                        $(".ui-widget.app.calendar #calendar").addClass("ui-helper-hidden");
                        $(".ui-widget.app.calendar #calendarlist-pnl-specialview").removeClass("ui-helper-hidden");
                        $(".ui-btn-toggle.month").removeClass("active");
                        $(".ui-btn-toggle.list").addClass("active");
                    break;
                }
            }
        },

        "AllyClick": function(event) {
            if(event.type == "click") {
                return true;
            } else if(event.type == "keypress" || event.type == "keydown" && (event.keyCode == this.KeyCodes.space || event.keyCode == this.KeyCodes.enter)) {
                return true;
            } else {
                return false;
            }
        },

        "GetBreakPoint": function() {
            return window.getComputedStyle(document.querySelector("body"), ":before").getPropertyValue("content").replace(/"|'/g, "");/*"*/
        }
    };

