// APP METHODS
        "CalendarListView": function() {
            if($(".ui-widget.app.calendar").length) {
                // AUTO CLICK FUNCTION FOR SCHOOLWIRES CALENDAR LIST VIEW FOR SMALL MOBILE DEVICES
                if(!$(".ui-widget.app.calendar #calendarlist-pnl-specialview.loaded").length){
                    $("a.ui-btn-toggle.list").click();
                    $("a.ui-btn-toggle.month").click();
                    $(".ui-widget.app.calendar #calendarlist-pnl-specialview").addClass("loaded");
                }

                switch(this.GetBreakPoint()) {
                    case "desktop": 
                    case "768": 
                    case "640":
                        $(".ui-widget.app.calendar #calendar").removeClass("ui-helper-hidden");
                        $(".ui-widget.app.calendar #calendarlist-pnl-specialview").addClass("ui-helper-hidden");
                    break;
                    case "480": 
                    case "320":
                        $(".ui-widget.app.calendar #calendar").addClass("ui-helper-hidden");
                        $(".ui-widget.app.calendar #calendarlist-pnl-specialview").removeClass("ui-helper-hidden");
                        $(".ui-btn-toggle.month").removeClass("active");
                        $(".ui-btn-toggle.list").addClass("active");
                    break;
                }
            }
        },
        "UpcomingEvents": function() {
        	/* Commented out by Brenton Kelly via custom hourly project
        	if (!$(".upcomingevents .joel-month").length) {
                $(".upcomingevents").modEvents({
                    columns     : "yes",
                    monthLong   : "yes",
                    dayWeek     : "yes"
                });

                var countLi = 0;
                $(".upcomingevents .ui-articles .ui-article").each(function(){
                    if (!$(this).find("h1.ui-article-title.sw-calendar-block-date").size()){
                        var moveArticle = $(this).html();
                        $(this).parent().prev().children().children().next().append(moveArticle);
                        $(this).parent().remove();
                    };
                });

                $(".upcomingevents .ui-articles .ui-article").each(function(){
                    var newDateTime = $('.upcoming-column.left h1', this).html().toLowerCase().split("");
                    var newDate = '';
                    var dateIndex = ((newDateTime.length > 2) ? 2 : 1); //if the dayWeek is set to yes we need to account for that in our array indexing

                    //add a zero to the date if it is a single digit
					if( newDateTime[dateIndex].length == 1 ) newDateTime[dateIndex] = "0" + newDateTime[dateIndex];

                    //if we have the day of the week make sure to include it.
                    if( dateIndex > 1 ){ 
                        newDate += newDateTime[0] + "";
                    }

                    //add in the month
                    newDate += "
" + newDateTime[dateIndex - 1] + ''; //the month is always the in the left array position to the date

                    //wrap the date in a new tag
                    newDate += '' + newDateTime[dateIndex] + '';

                    //append the date and month back into their columns
                    $('.upcoming-column.left h1', this).html(newDate);

                    //add an ALL DAY label if no time was given
                    $('.upcoming-column.right .ui-article-description', this).each(function(){
                        if( $('.sw-calendar-block-time', this).length < 1 ){ //if it doesnt exist add it
                            $(this).prepend('ALL DAY');
                        }

                        //$('.sw-calendar-block-title', this).after($('.sw-calendar-block-time', this));
                    });
                });

                $("#upcoming-events-slider div.ui-widget.app.upcomingevents").each(function() {
                	var pmi = $(this).closest("div[id*='pmi-']").attr("id").replace("pmi-", "");
                    $(".ui-articles", this).wrap('
');
                    $(".ui-article", this).each(function(i) {
                    	if($(".ui-article-description", this).length > 4) {
                        	$(".upcoming-column.right", this).append('View More');
                        }

                    	$("p:nth-of-type(n+5)", this).wrapAll('
');
                    });
                });

                $(document).on("click keydown", ".cs-more-events-btn", function(e) {
                	if(CreativeTemplate.AllyClick(e)) {
                    	e.preventDefault();

                        var dropdownId = $(this).attr("data-dropdown-id");

                        if($(this).attr("aria-expanded") == "false") {
                        	$(this).attr("aria-expanded", "true");
                            $("#" + dropdownId).slideDown();
                        } else {
                        	$(this).attr("aria-expanded", "false");
                            $("#" + dropdownId).slideUp();
                        }
                    }
                });
            }
            */
            var longMonths = ["January", "February", "March", "April", "May", "June", "July", "August", "September", "October", "November", "December"];
            var shortMonths = ["Jan.", "Feb.", "Mar.", "Apr.", "May", "Jun.", "Jul.", "Aug.", "Sep.", "Oct.", "Nov.", "Dec."];
            var longDays = ["Sunday", "Monday", "Tuesday", "Wednesday", "Thursday", "Friday", "Saturday"];
            var shortDays = ["Sun.", "Mon.", "Tues.", "Wed.", "Thur.", "Fri.", "Sat."];
            $(".ui-widget.app.upcomingevents").modEvents({
				columns     : "no",
				monthLong   : "yes",
				dayWeek     : "yes"
			});

			$(".ui-widget.app.upcomingevents .ui-article").each(function() {
				if (!$("h1.ui-article-title.sw-calendar-block-date", this).length){
					var moveArticle = $(this).html();
					$(this).parent().prev("li").find(".ui-article").append(moveArticle);
					$(this).parent().remove();
				}
			});

            $(".ui-widget.app.upcomingevents .ui-article-description").each(function() {    
                $(this).prev().children(".joel-day, .joel-month").wrapAll('');

            	if($(".sw-calendar-block-time", this).length) {
                    $(".sw-calendar-block-time", this).html($(".sw-calendar-block-time", this).text().replace(/ AM/g, " a.m."));
                	$(".sw-calendar-block-time", this).html($(".sw-calendar-block-time", this).text().replace(/ PM/g, " p.m."));
                    $(this).append($(".sw-calendar-block-time", this).clone());
                    //$(".sw-calendar-block-time", this).html($(".sw-calendar-block-time", this).text().replace(/ - /g, "-"));
                } else {
					$(this).prepend('All Day').append('All Day');
                }
            });

            $(".upcomingevents .sw-calendar-block-title a").focus(function() {
                $(this).parents("li").addClass("focused");
            });

            $(".upcomingevents .sw-calendar-block-title a").blur(function() {
              	$(this).parents("li").removeClass("focused");                  
            });

            $("#upcoming-events-slider div.ui-widget.app.upcomingevents").each(function() {
                var pmi = $(this).closest("div[id*='pmi-']").attr("id").replace("pmi-", "");
                //$(".ui-articles", this).wrap('
');
                $(".ui-article", this).each(function(i) {
                    if($(".ui-article-description", this).length > 4) {
                        $(this).append('
View More');
                    }

                    $("p:nth-of-type(n+5)", this).wrapAll('
');
                });
            });

            $("div.ui-widget.app.upcomingevents").each(function() {
                $(".ui-article", this).each(function(i) {
                	var dates = $(".sw-calendar-block-date .joel-dates", this).detach();
                    var eventDate = $(".sw-calendar-block-date", this).text();
                    $(".sw-calendar-block-date", this).empty().append(dates).append('' + eventDate + '');

                    $(".joel-day", this).addClass("desktop");
                    $('' + shortDays[longDays.indexOf($(".joel-day", this).text())] + '').insertAfter($(".joel-day", this));
                    $(".joel-month", this).addClass("desktop");
                    $('' + shortMonths[longMonths.indexOf($(".joel-month", this).text())] + '').insertAfter($(".joel-month", this));
                });
            });

            $(document).on("click keydown", ".cs-more-events-btn", function(e) {
                if(CreativeTemplate.AllyClick(e)) {
                    e.preventDefault();

                    var dropdownId = $(this).attr("data-dropdown-id");

                    if($(this).attr("aria-expanded") == "false") {
                        $(this).attr({
                        	"aria-expanded": "true",
                        	"aria-label": $(this).attr("aria-label").replace("view more", "hide overflow")
                        });
                        $("#" + dropdownId).slideDown().attr("aria-hidden", "false");
                        $(this).text("View Less");
                    } else {
                        $(this).attr({
                        	"aria-expanded": "false",
                        	"aria-label": $(this).attr("aria-label").replace("hide overflow", "view more")
                        });
                        $("#" + dropdownId).slideUp().attr("aria-hidden", "true");
                        $(this).text("View More");
                    }
                }
            });
        },
        "HeadlineCards": function(){
            if( $('#headline-slider .app.headlines').length > 0 ){
                $('#headline-slider .app.headlines .ui-articles > li').each(function(k2, v2){
                    if(!$('.ui-article', v2).hasClass('background-set')){
                        $('.ui-article', v2).wrap('
');
                        if( $('.ui-article-thumb img', v2).attr('src') == undefined || $('.ui-article-thumb img', v2).attr('src') == "../../" ){
                            $('.ui-article', v2).append('

');
                        }else{
                            $('.ui-article-thumb', v2).addClass('remove').before('
' + $('.ui-article-thumb', v2).html() + '');
                            $('.ui-article-thumb.remove', v2).remove();
                            $('.ui-article-image', v2).append('
');
                        } 

                        $('.ui-article-title', v2).html($('.ui-article-title a', v2).html());
                        $('.ui-article-title a', v2).remove();
                        $('.card-info > div', v2).append($('.ui-article-title', v2));
                        $('.ui-article-description', v2).remove();

                        $('.ui-article', v2).addClass('background-set');
                    }
                });
            }
        },
