	$(function() {
    	ContentTranslationApp7214.Init();
    });

    var ContentTranslationApp7214 = {
    	// PROPERTIES
        "KeyCodes": { "tab": 9, "enter": 13, "esc": 27, "space": 32, "end": 35, "home": 36, "left": 37, "up": 38, "right": 39, "down": 40 },
        "TabView": false,

    	// METHODS
        "Init": function() {
        	var _this = this;

        	$(".panel-content-wrapper-textarea").each(function() {
            	$(this).parent().html($(this).val());
            });

            $("#pmi-7214 .ui-widget-detail img").each(function() {
                if ($(this).attr('width') !== undefined && $(this).attr('height') !== undefined) { // IMAGE HAS INLINE DIMENSIONS
                    $(this).css({"display": "inline-block", "width": "100%", "max-width": $(this).attr("width") + "px", "height": "auto", "max-height": $(this).attr("height") + "px"});
                }
            });

            //this.CheckDefaults();
        	this.UiEvents();

            //$(window).resize(function() { _this.WindowResize(); });
        },

        "CheckDefaults": function() {
        	switch(this.GetBreakPoint()) {
            	case "960":
                case "768":
                case "640":
                	this.TabView = true;
                break;

                case "480":
                case "320":
                	this.TabView = false;
                break;
            }
        },

        "UiEvents": function() {
        	var _this = this;

            // TAB CLICK
            $(document).on("click keydown", "#pmi-7214 .content-multilingual-tab", function(e) {
            	if(_this.AllyClick(e)) {
                	if(!$(this).hasClass("active")) {
                    	// SWITCH TAB
                    	_this.SwitchTab(this);
                	}
                } else {
                	var lastIndex = $("#pmi-7214 .content-multilingual-tab").length - 1;

                    // ONLY CONSUME IF NOT USING ALT KEY
                    if (!e.altKey) {
                        switch(e.keyCode) {
                            // MOVE ONE STEP BACKWARDS
                            case _this.KeyCodes.left:
                            case _this.KeyCodes.up:
                                if (!e.ctrlKey) { // CTRL+ARROW MOVES FOCUS FROM PANEL TO OPEN TAB - MAKE SURE THAT IS NOT BEING PRESSED TOO
                                    e.preventDefault();
                                    e.stopPropagation();

                                    // GET THE PREVIOUS TAB
                                    var prevTab;
                                    if(parseInt($(this).attr("data-index")) == 0) {
                                        prevTab = $("#pmi-7214 .content-multilingual-tab[data-index='" + lastIndex + "']"); // LAST TAB
                                    } else { 
                                        prevTab = $("#pmi-7214 .content-multilingual-tab[data-index='" + (parseInt($(this).attr("data-index")) - 1) + "']"); // PREVIOUS TAB
                                    } 

                                    // SWITCH TAB
                                    _this.SwitchTab(prevTab);
                                }
                            break;

                            // MOVE ONE STEP FORWARDS
                            case _this.KeyCodes.right:
                            case _this.KeyCodes.down:
                                if (!e.ctrlKey) { // CTRL+ARROW MOVES FOCUS FROM PANEL TO OPEN TAB - MAKE SURE THAT IS NOT BEING PRESSED TOO
                                    e.preventDefault();
                                    e.stopPropagation();

                                    // GET THE NEXT TAB
                                    var nextTab;
                                    if (parseInt($(this).attr("data-index")) == lastIndex) { 
                                        nextTab = $("#pmi-7214 .content-multilingual-tab[data-index='0']"); // FIRST TAB
                                    } else { 
                                        nextTab = $("#pmi-7214 .content-multilingual-tab[data-index='" + (parseInt($(this).attr("data-index")) + 1) + "']"); // PREVIOUS TAB
                                    } 

                                    // SWITCH TAB
                                    _this.SwitchTab(nextTab);
                                }
                            break;

                            // JUMP TO FIRST TAB
                            case _this.KeyCodes.home:
                                e.preventDefault();
                                e.stopPropagation();

                                // SWITCH TAB
                                _this.SwitchTab($("#pmi-7214 .content-multilingual-tab[data-index='0']"));
                            break;

                            // JUMP TO LAST TAB
                            case _this.KeyCodes.end:
                                e.preventDefault();
                                e.stopPropagation();

                                // SWITCH TAB
                                _this.SwitchTab($("#pmi-7214 .content-multilingual-tab[data-index='" + lastIndex + "']"));
                            break;
                        }
                    }
                }
            });

            // ACCORDION CLICK
            $(document).on("click keydown", "#pmi-7214 .content-multilingual-panel-button", function(e) {
            	if(_this.AllyClick(e)) {
                	e.preventDefault();

                    if(!$(this).hasClass("active")) {
                    	// CLOSE ACTIVE PANEL
                        $("#pmi-7214 .content-multilingual-panel-button.active").removeClass("active").attr("aria-expanded", "false");
                        $("#pmi-7214 .panel-content-wrapper.active").slideUp(function() {
                        	$(this).removeClass("active").removeAttr("style");
                        });

                        // OPEN NEW PANEL
                        $(this).addClass("active").attr("aria-expanded", "true");
                        $("+ .panel-content-wrapper", this).slideDown(function() {
                        	$(this).addClass("active").removeAttr("style");
                        });
                    }
                }
            });
        },

        "SwitchTab": function(element) {
        	$("#pmi-7214 .content-multilingual-tab.active").attr({"aria-selected": "false", "tabindex": "-1"}).removeClass("active");
			$(element).attr({"aria-selected": "true", "tabindex": "0"}).addClass("active").focus();

            // SWITCH PANEL
            this.SwitchPanel(element);
        },

        "SwitchPanel": function(element) {
        	$("#pmi-7214 .content-multilingual-panel.active").attr("aria-hidden", "true").removeClass("active");
            $("#pmi-7214 .content-multilingual-panel[data-index='" + $(element).attr("data-index") + "']").attr("aria-hidden", "false").addClass("active");
        },

        "AllyClick": function(event) {
            if(event.type == "click") {
                return true;
            } else if(event.type == "keydown" && (event.keyCode == this.KeyCodes.space || event.keyCode == this.KeyCodes.enter)) {
                return true;
            } else {
                return false;
            }
        },

        "GetBreakPoint": function() {
            return window.getComputedStyle(document.getElementById("pmi-7214"), ":before").getPropertyValue("content").replace(/"|'/g, "");
        },

        "WindowResize": function() {
        	var _this = this;

        	switch(this.GetBreakPoint()) {
            	case "960":
                case "768":
                case "640":
                	if(this.TabView == false) {
                        $("#pmi-7214 .content-multilingual-tab").removeClass("active").attr({"aria-selected": "false", "tabindex": "-1"});
                        $("#pmi-7214 .content-multilingual-panel").removeClass("active").attr("aria-hidden", "true");
                        $("#pmi-7214 .content-multilingual-tab:first-child").addClass("active").attr({"aria-selected": "true", "tabindex": "0"});
                        $("#pmi-7214 .content-multilingual-panel:first-child").addClass("active").attr("aria-hidden", "false");

                        $("#pmi-7214 .content-multilingual-panel-button").removeClass("active").attr("aria-expanded", "false");
                        $("#pmi-7214 .panel-content-wrapper").removeClass("active");

                        this.TabView = true;
                    }
                break;

                case "480":
                case "320":
                	if(this.TabView == true) {
                        $("#pmi-7214 .content-multilingual-tab").removeClass("active").attr({"aria-selected": "false", "tabindex": "-1"});
                        $("#pmi-7214 .content-multilingual-panel").removeClass("active").attr("aria-hidden", "false");

                        $("#pmi-7214 .content-multilingual-panel:first-child .content-multilingual-panel-button").addClass("active").attr("aria-expanded", "true");
                        $("#pmi-7214 .content-multilingual-panel:first-child .panel-content-wrapper").addClass("active");

                        this.TabView = false;
                    }
                break;
            }
        }

    };

