     if (window.location.href.indexOf("SiteID=30") > -1) {
         $('#swlogin').find('.ui-widget-toolbar-bottom').html(myCustomContentHTML2); 
     } else { 
         $('#swlogin').find('.ui-widget-toolbar-bottom').html(myCustomContentHTML1); 
     }
  } 
});

