    function mystartUserOptions() {
    	$(".user-options").attr({
        	"role": "button",
            "aria-expanded": "false",
            "aria-haspopup": "true",
            "tabindex": "0"
        });
        $(".user-dropdown").attr({
        	"aria-hidden": "true"
        });

        $(".user-options").click(function() {
        	if($(this).hasClass("open")) {
            	$(this).removeClass("open").attr({
                    "aria-expanded": "true",
                    "aria-haspopup": "false",
                });
                $(".user-dropdown").slideUp().attr({
                    "aria-hidden": "true"
                });
                $(".user-dropdown > div > a").attr("tabindex","-1");
            } else {
            	$(this).addClass("open").attr({
                    "aria-expanded": "true",
                    "aria-haspopup": "false",
                });
                $(".user-dropdown").slideDown().attr({
                    "aria-hidden": "false"
                });
                $(".user-dropdown > div > a").attr("tabindex","0");
            }
        });
        $(".user-options").keydown(function(e) {
            // CAPTURE KEY CODE
            switch(e.keyCode) {
                // CONSUME SPACE AND ENTER KEYS
                case csKeyCodes.space:
                case csKeyCodes.enter:
                	e.preventDefault();
                	if($(this).hasClass("open")) {
                        $(this).removeClass("open").attr({
                            "aria-expanded": "true",
                            "aria-haspopup": "false"
                        });
                        $(".user-dropdown").slideUp().attr({
                            "aria-hidden": "true"
                        });
                        $(".user-dropdown > div > a").attr("tabindex","-1");
                    } else {
                        $(this).addClass("open").attr({
                            "aria-expanded": "true",
                            "aria-haspopup": "false"
                        });
                        $(".user-dropdown").slideDown().attr({
                            "aria-hidden": "false"
                        });
                        $(".user-options #ui-btn-sitemanager").focus();
                        $(".user-dropdown > div > a").attr("tabindex","0");
                    }
                break;
            }
        });
        $(".user-options").hover(function() {

        }, function() {
        	$(this).removeClass("open").attr({
                "aria-expanded": "true",
                "aria-haspopup": "false"
            });
            $(".user-dropdown").slideUp().attr({
                "aria-hidden": "true"
            });
        });

        $(".user-dropdown .sw-mystart-button > a").keydown(function(e) {
            // CAPTURE KEY CODE
			switch(e.keyCode) {
				// CONSUME LEFT AND UP ARROWS
				case csKeyCodes.left:
				case csKeyCodes.up:
					e.preventDefault();
					// IS FIRST ITEM
					if($(this).parent().is(":first-child")) {
						// FOCUS FIRST ITEM
						$(".user-dropdown div:last-child > a").focus();
					} else {
						// FOCUS NEXT ITEM
						$(this).parent().prev("div").find("> a").focus();
					}
				break;

				// CONSUME RIGHT AND DOWN ARROWS
				case csKeyCodes.right:
				case csKeyCodes.down:
					e.preventDefault();
					// IS LAST ITEM
					if($(this).parent().is(":last-child")) {
						// FOCUS FIRST ITEM
						$(".user-dropdown div:first-child > a").focus();
					} else {
						// FOCUS NEXT ITEM
						$(this).parent().next("div").find("> a").focus();
					}
				break;

				// CONSUME HOME KEY
				case csKeyCodes.home:
					e.preventDefault();

					// FOCUS FIRST ITEM
					$(".user-dropdown div:first-child > a").focus();
				break;

				// CONSUME END KEY
				case csKeyCodes.end:
					e.preventDefault();

					// FOCUS LAST ITEM
					$(".user-dropdown div:last-child > a").focus();
				break;

                // CONSUME TAB KEY
				case csKeyCodes.tab:
					e.preventDefault();
					$("#gb-mystart .gb-school-list-btn").focus();
                    $(".user-options").removeClass("open").attr({
                        "aria-expanded": "true",
                        "aria-haspopup": "false"
                    });
                    $(".user-dropdown").slideUp().attr({
                        "aria-hidden": "true"
                    });
                    $(".user-dropdown > div > a").attr("tabindex","-1");

				break;

			}
		});
    }

    function schoolsActions() {
    	$("#gb-mystart .gb-school-list-btn").attr({
        	"role": "button",
            "aria-expanded": "false",
            "aria-haspopup": "true",
            "tabindex": "0"
        });
        $("#gb-mystart .gb-school-sections").attr({
        	"aria-hidden": "true"
        });

        $("#gb-mystart #gb-school-list").on("click", function() {
        	if($(".gb-school-list-btn",this).hasClass("open")) {
            	$(".gb-school-list-btn",this).removeClass("open").attr({
                    "aria-expanded": "true",
                    "aria-haspopup": "false",
                });
                $("#gb-mystart .gb-school-sections").slideUp().attr({
                    "aria-hidden": "true"
                });
            } else {
            	$(".gb-school-list-btn",this).addClass("open").attr({
                    "aria-expanded": "true",
                    "aria-haspopup": "false",
                });
                $("#gb-mystart .gb-school-sections").slideDown().attr({
                    "aria-hidden": "false"
                });
            }
        });

            $("#gb-mystart #gb-school-list").hover(function() {

            }, function() {
            	if($(".gb-school-list-btn",this).hasClass("open")) {
                    $(".gb-school-list-btn",this).removeClass("open").attr({
                        "aria-expanded": "true",
                        "aria-haspopup": "false",
                    });
                    $("#gb-mystart .gb-school-sections").slideUp().attr({
                        "aria-hidden": "true"
                    });
                }
            });

            $("#gb-mystart .gb-school-list-btn").keydown(function(e) {
            	switch(e.keyCode) {
                	case csKeyCodes.up:
