                    case csKeyCodes.up:
                        e.preventDefault();
                        // IS FIRST ITEM
                        if($(this).parent().is(":first-child")) {
                            // FOCUS FIRST ITEM
                            $(".gb-school-list.high li:last-child > a").focus();
                        } else {
                            // FOCUS NEXT ITEM
                            $(this).parent().prev("li").find("> a").focus();
                        }
                    break;

                    // CONSUME RIGHT AND DOWN ARROWS
                    case csKeyCodes.right:
                    case csKeyCodes.down:
                        e.preventDefault();
                        // IS LAST ITEM
                        if($(this).parent().is(":last-child")) {
                            // FOCUS FIRST ITEM
                            $(".gb-school-list.specialty-two li:first-child > a").focus();
                        } else {
                            // FOCUS NEXT ITEM
                            $(this).parent().next("li").find("> a").focus();
                        }
                    break;

                    // CONSUME HOME KEY
                    case csKeyCodes.home:
                        e.preventDefault();

                        // FOCUS FIRST ITEM
                        $(".gb-school-list.one li:first-child > a").focus();
                    break;

                    // CONSUME END KEY
                    case csKeyCodes.end:
                        e.preventDefault();

                        // FOCUS LAST ITEM
                        $(".gb-school-list.specialty-two li:last-child > a").focus();
                    break;

                    // CONSUME TAB KEY
                    case csKeyCodes.tab:
                        e.preventDefault();
                        $(".gb-school-sections.channel").addClass("hidden").attr({
                            "aria-hidden": "true",
                            "aria-expanded": "false"
                        });
                        $("#gb-search-btn").focus();
                    break;

                }
            });
            $(".gb-school-list.specialty-two li a").keydown(function(e) {
                // CAPTURE KEY CODE
                switch(e.keyCode) {
                    // CONSUME LEFT AND UP ARROWS
                    case csKeyCodes.left:
                    case csKeyCodes.up:
                        e.preventDefault();
                        // IS FIRST ITEM
                        if($(this).parent().is(":first-child")) {
                            // FOCUS FIRST ITEM
                            $(".gb-school-list.specialty-one li:last-child > a").focus();
                        } else {
                            // FOCUS NEXT ITEM
                            $(this).parent().prev("li").find("> a").focus();
                        }
                    break;

                    // CONSUME RIGHT AND DOWN ARROWS
                    case csKeyCodes.right:
                    case csKeyCodes.down:
                        e.preventDefault();
                        // IS LAST ITEM
                        if($(this).parent().is(":last-child")) {
                            // FOCUS FIRST ITEM
                            $(".gb-school-list.one li:first-child > a").focus();
                        } else {
                            // FOCUS NEXT ITEM
                            $(this).parent().next("li").find("> a").focus();
                        }
                    break;

                    // CONSUME HOME KEY
                    case csKeyCodes.home:
                        e.preventDefault();

                        // FOCUS FIRST ITEM
                        $(".gb-school-list.one li:first-child > a").focus();
                    break;

                    // CONSUME END KEY
                    case csKeyCodes.end:
                        e.preventDefault();

                        // FOCUS LAST ITEM
                        $(".gb-school-list.specialty-two li:last-child > a").focus();
                    break;

                    // CONSUME TAB KEY
                    case csKeyCodes.tab:
                        e.preventDefault();
                        $("#gb-mystart .gb-school-list-btn").removeClass("open").attr({
                            "aria-expanded": "true",
                            "aria-haspopup": "false"
                        });
                        $("#gb-mystart .gb-school-sections").slideUp().attr({
                            "aria-hidden": "true"
                        });
                        $(".sw-mystart-dropdown.translate .selector").focus();
                    break;

                }
            });

        $("#gb-mystart .gb-school-list-btn").keydown(function(e) {
            // CAPTURE KEY CODE
            switch(e.keyCode) {
                // CONSUME SPACE AND ENTER KEYS
                case csKeyCodes.space:
                case csKeyCodes.enter:
                    e.preventDefault();
                    if($(this).hasClass("open")) {
                        $(this).removeClass("open").attr({
                            "aria-expanded": "true",
                            "aria-haspopup": "false",
                        });
                        $("#gb-mystart .gb-school-sections").slideUp().attr({
                            "aria-hidden": "true"
                        });
                    } else {
                        $(this).addClass("open").attr({
                            "aria-expanded": "true",
                            "aria-haspopup": "false",
                        });
                        $("#gb-mystart .gb-school-sections").slideDown().attr({
                            "aria-hidden": "false"
                        });
                        $("#gb-mystart .gb-school-list.one li:first-child > a").focus();
                    }
                break;
            }
        });
        $("#gb-mystart #gb-school-list").hover(function() {

        }, function() {
            $(this).removeClass("open").attr({
                "aria-expanded": "true",
                "aria-haspopup": "false"
            });
            $("#gb-mystart .gb-school-sections").slideUp().attr({
                "aria-hidden": "true"
            });
        });

        /*$(".gb-school-list li a").keydown(function(e) {
            switch(e.keyCode){
                // CONSUME LEFT AND UP ARROWS
                case csKeyCodes.tab:
                    e.preventDefault();
                    $("#gb-mystart .gb-school-list-btn").removeClass("open").attr({
                        "aria-expanded": "true",
                        "aria-haspopup": "false"
                    });
                    $("#gb-mystart .gb-school-sections").slideUp().attr({
                        "aria-hidden": "true"
                    }); 
                    $(".sw-mystart-dropdown.translate .selector").focus();
                break;
            }
        });*/
    }

