    function searchMenu() {
    	$("#sw-mystart-search").prependTo("#search-dropdown");

    	$("#gb-search-btn").click(function(e) {
        	e.preventDefault();
        	var searchBtn = this;
        	if($("#search-dropdown").hasClass("open")) {
            	$("#gb-search-btn").removeClass("open").attr({
                        "aria-hidden": "true"
                });
                $("#search-dropdown").removeClass("open").attr({
                    	"aria-expanded": "false",
                }).slideUp();
                $("#sw-search-input").attr("tabindex","-1");
            } else {
            	$("#gb-search-btn").addClass("open").attr({
                        "aria-hidden": "false"
                });
                $("#search-dropdown").addClass("open").attr({
                    	"aria-expanded": "true",
                }).slideDown();
                $("#sw-search-input").attr("tabindex","0");
            }
        });
        $("#gb-search-btn").keydown(function(e) {
        	if(e.keyCode == csKeyCodes.enter || e.keyCode == csKeyCodes.space) {
                e.preventDefault();
                var searchBtn = this;
                if($("#search-dropdown").hasClass("open")) {
                    $("#gb-search-btn").removeClass("open").attr({
                            "aria-hidden": "true"
                    });
                    $("#search-dropdown").removeClass("open").attr({
                            "aria-expanded": "false",
                    }).slideUp();
                    $("#sw-search-input").attr("tabindex","-1");
                } else {
                    $("#gb-search-btn").addClass("open").attr({
                            "aria-hidden": "false"
                    });
                    $("#search-dropdown").addClass("open").attr({
                            "aria-expanded": "true",
                    }).slideDown();
                    $("#sw-search-input").attr("tabindex","0");
                    setTimeout(function() {
                        $("#sw-search-input").focus();
                    }, 300);
                }
            }
        });
        $("#search-dropdown").focusout(function() {
            	setTimeout(function() {
                    if(!$("#search-dropdown").find(":focus").length && !$("#gb-search-panel-toggle").is(":focus")) {
                        $("#b-search-btn").attr("aria-expanded", "false");
                        $("#search-dropdown").attr("aria-hidden", "true").slideUp();
                    }
                }, 500);
            }).keydown(function(e) {
            	if(e.keyCode == this.KeyCodes.esc) {
                	e.preventDefault();

                    $("#gb-search-btn").attr("aria-expanded", "false").focus();
                    $("#search-dropdown").attr("aria-hidden", "true").slideUp();
                }
            });
    }

	function pgScreenSize(){
		var getWidth = $(window).width();
		if (getWidth >= 1024) { // 960+
			//photoGallery();
			sponsorGallery();
		} if (getWidth >= 768 && getWidth < 1024) { // 768+
			//photoGallery();
			sponsorGallery();
			//appView768();
		} if (getWidth >= 640 && getWidth < 768) { // 640+
			//photoGallery();
			sponsorGallery();
			//appView768();
		} if (getWidth >= 480 && getWidth < 640) { // 480+
			//photoGallery();
			sponsorGallery();
			//appView();
		} if (getWidth >= 320 && getWidth < 480) { // 320+
			//photoGallery();
			sponsorGallery();
			//appView();
		}
	}

