    function MMGPlugin() {
    	var mmg = eval("multimediaGallery" + $("#sw-content-container18.ui-hp .ui-widget.app.multimedia-gallery:first").attr("data-pmi"));
        mmg.props.defaultGallery = false;

    	$("#sw-content-container18.ui-hp .ui-widget.app.multimedia-gallery:first").csMultimediaGallery({
            "efficientLoad" : true,
            "imageWidth" : 576,
            "imageHeight" : 385,
            "mobileDescriptionContainer": [960, 768, 640, 480, 320], // [960, 768, 640, 480, 320]
            "galleryOverlay" : false,
            "linkedElement" : [],  // ["image", "title", "overlay"]
            "playPauseControl" : true,
            "backNextControls" : true,
            "bullets" : false,
            "thumbnails" : false,
            "thumbnailViewerNum": [3, 4, 3, 3, 2], // NUMERICAL - [960 view, 768 view, 640 view, 480 view, 320 view]
            "autoRotate" : true,
            "hoverPause" : true,
            "transitionType" : "fade",
            "transitionSpeed" : 3,
            "transitionDelay" : 5,
            "fullScreenRotator" : false,
            "fullScreenBreakpoints" : [960], // NUMERICAL - [960, 768, 640, 480, 320]
            "onImageLoad" : function(props) {}, // props.element, props.recordIndex, props.mmgRecords
            "allImagesLoaded" : function(props) {}, // props.element, props.mmgRecords
            "onTransitionStart" : function(props) {}, // props.element, props.currentRecordIndex, props.currentGalleryIndex, props.nextRecordIndex, props.nextGalleryIndex, props.mmgRecords
            "onTransitionEnd" : function(props) {}, // props.element, props.currentRecordIndex, props.currentGalleryIndex, props.mmgRecords
            "allLoaded" : function(props) {
            	if(!$(".mmg-controls-cont").length) {
                	$(".mmg-slides-outer").after("
");
                }

                if($(".mmg-description-outer").length) {
                	$(".mmg-controls-cont").removeClass("no-desc").prependTo(".mmg-description-outer");    
                }
                if(!$(".mmg-controls-cont .mmg-control.next").length) {
                	$(".mmg-control.next").appendTo(".mmg-controls-cont");
                }
                if(!$(".mmg-controls-cont .mmg-control.play-pause").length) {
                	$(".mmg-control.play-pause").prependTo(".mmg-controls-cont");
                }
                if(!$(".mmg-controls-cont .mmg-control.back").length) {
                	$(".mmg-control.back").prependTo(".mmg-controls-cont");
                }
            }, // props.element, props.mmgRecords
            "onWindowResize": function(props) {} // props.element, props.mmgRecords
        });
    }

