			$("#gb-school-list").append($(".gb-school-sections"));
			$(".gb-school-sections").addClass("mystart hidden");
			$(".gb-school-sections").clone().removeClass("mystart").addClass("channel").hide();
			/*$(".gb-school-list-btn").click(function(){
				if($(".gb-school-sections.mystart").hasClass("hidden")) {
