$(document).ready(function(){
   var beaconURL='https://analytics.schoolwires.com/analytics.asmx/Insert?AccountNumber=syx1%2fsGyaygLvqhJcIug7g%3d%3d&SessionID=d1ff0e51-a669-4e53-bd09-f6554a1179d9&SiteID=4535&ChannelID=0&SectionID=4535&PageID=24338&HitDate=4%2f22%2f2020+6%3a58%3a00+AM&Browser=Unknown+0.0&OS=Unknown&IPAddress=10.61.89.16';
