	$( document ).ready(function() {
		if(location.search.indexOf("PageType=14&DomainID=4&PageID=1&ModuleInstanceID=25") !=-1){
			year1 = ($('[aria-label="Filter By Month"] li:contains(' + (new Date()).getFullYear() + ')').length > 0) ? (new Date()).getFullYear() : (new Date()).getFullYear() - 1;//global variable
			setupByMonthFilter(year1);
		}

		//accessibility tweaks
		$('#sw-search-button').removeAttr('aria-label');
		$('#sw-search-button > span').attr('aria-label', 'Search Our Site');
		$('.sw-skipnav-outerbar > #skipLink').removeAttr('tabindex');

		$('.sw-channel-dropdown').attr('role', 'group');
		var menuIndex = ['About Us','Departments','Board of Education','Academics','Get Involved','Athletics','Students & Families','Community','Staff Hub'];
		var countMenuIndex = 0;
		$('.sw-channel-dropdown').each(function(){
			if(countMenuIndex < menuIndex.length)
			$(this).attr('aria-label', menuIndex[countMenuIndex]);
			countMenuIndex++;
		});

		$('#navc-22 > a > span').contents().unwrap();
		$('#navc-22 > a').css('display','inherit');
		$('#sw-search-input').attr('autocomplete', 'off');
		$('#sw-search-input').bind('focus', function () {
			$('#sw-mystart-search').addClass('textbox-shadow-onfocus');
		});
		$('#sw-search-input').bind('blur', function () {
			$('#sw-mystart-search').removeClass('textbox-shadow-onfocus');
		});		
	});

	function setupByMonthFilter(year){
		//Hide news post not from current year		
		var insertBeforeStr = '';
		var output = [];

		//check if nothing posted this year and exclude, e.g. begining of the year
		var y = ($('[aria-label="Filter By Month"] li:contains(' + (new Date()).getFullYear() + ')').length > 0) ? (new Date()).getFullYear() : (new Date()).getFullYear() - 1;

		for(var i = y; i >= 2015; i--){
			output.push(''+ i +'');
		}		
		$(insertBeforeStr).insertAfter('[aria-label="Filter By Month"]');//id1234		
		$('#drpYearFilter').html(output.join(''));
		filterByYear(year);		

		//add update call	
		$('a[onclick*=".focus(); }, 200); return false;"]').each(function(){ 
            var oldUrl = $(this).attr("onclick"); 
            var newUrl = oldUrl.replace(".focus(); }, 200); return false;", " .focus(); }, 200); setTimeout(function() { setupByMonthFilter(year1); }, 1000); return false;"); // Create new url
            $(this).attr("onclick", newUrl); 			
        });
	}

	function filterByYear(year){	
		//console.log(year);
		$('[aria-label="Filter By Month"] li').show();	
		$('[aria-label="Filter By Month"] li:not(:contains(' + year + '))').hide();	

		//highlight active month
		if($('[aria-label="Filter By Month"] li[class^="ui-group-active"]').length > 0){
			$('[aria-label="Filter By Month"] li[class^="ui-group-active"]').css('font-weight','bold');			
		}else{
			$('[aria-label="Filter By Month"] li:visible:first').css('font-weight','bold');
		}

	}

	$(window).load(function(){
		if($(window).width() >= 320 && $(window).width() <= 639){
			calendarListView();
		}
		searchText();

		$("div.ui-widget.app.navigation li div.bullet").click(function(){
			return true;
		});

	});

    $(window).scroll(function(){
    	stickyCalendarFix();
    });


	$(window).load(function() {
		//addFramesetTranslate();

		//add global target blank for external links
		$('a[href^="http"]').attr('target', function() {
		  if(this.host == location.host) return '_self'
		  else return '_blank'
		});

		//add globally title from placeholder for accessibility
		setTimeout(function () {
			$('input[placeholder]').attr('title', function() {
			  if(!(this.placeholder == null || this.placeholder == ''))  
			  return this.placeholder;
			});
		}, 1000);	

		//perform global mail decode if needed
		$('a[href^="mailto:"]').attr('target1', function() {
		  if(location.search.indexOf('enc=1')==-1 && (this.href.indexOf("rguf.x12.vy.hf") != -1 || this.href.indexOf("rguf202.bet") != -1)) {			
			var unenc1 = swrot13(this.href.substring(7, this.length));
			this.href = "mailto:" + unenc1;
			if(this.text.indexOf('@')!=-1)
			this.text = unenc1;
		  }
		});
	});

	$(function(){
		// SET IE CLASSES TO BODY
		if(isMSIE){ $("body").addClass("msie"); }
		if(isMSIE8){ $("body").addClass("msie8"); }
		if(isMSIE9){ $("body").addClass("msie9"); }
		if(isMSIE10){ $("body").addClass("msie10"); }

		$(window).bind("resize", function(){ pgScreenSize();stickyCalendarFix(); });

		// RESPONSIVE MAIN MENU
		$("body").csRsMenu({
		        "breakPoint"            : 768, // SYSTEM BREAK POINTS - 768, 640, 480, 320
		        "slideDirection"        : "left-to-right", // OPTIONS - left-to-right, right-to-left   
		        "primaryColor"          : "#1C2452", // DEFAULT - #2C363E
		        "secondaryColor"        : "#263272", // DEFAULT - #222A30
		        "accentColor"           : "#37B0C8", // DEFAULT - #B5BBBF
		        "menuButtonParent"      : "#gb-channel-list",
		        "showMenuBtnText"       : "yes",
		        "menuBtnText"           : "MENU",
		        "showSchools"           : "no",
		        "showTranslate"         : "yes",
		        "translateId"           : "none",
		        "showAccount"           : "yes",
		        "extraMenuOptions"      : "Global Icons", // EXTRA MENU HEADING NAMES SEPARATED BY A ;
		        "extraMenuItems"        : "1, Calendar, /Page/2, Same Window;1, Contact Us, https://www.eths.k12.il.us/Domain/397, Same Window;1, Menus, http://schools.mealviewer.com/school/EvanstonHigh, New Window;1, Staff Directory, https://www.eths.k12.il.us/directory, Same Window;1, Web Store, https://eths.revtrak.net/tek9.asp?, New Window;", // EXTRA MENU BUTTONS - STRUCTURE IS: MENU-OPTION-NUMBER, ITEM-NAME, ITEM-URL, ITEM-TARGET;
		        "siteID"                : "4"
		});

		// RESPONSIVE PAGELIST MENU
		$("body").csRsPagelistMenu({
		        "breakPoint"            : 640, // SYSTEM BREAK POINTS - 768, 640, 480, 320
		        "slideDirection"        : "left-to-right", // OPTIONS - left-to-right, right-to-left
		        "primaryColor"          : "#1C2452", // DEFAULT - #2C363E
		        "secondaryColor"        : "#263272", // DEFAULT - #222A30
		        "accentColor"           : "#37B0C8", // DEFAULT - #B5BBBF
		        "menuButtonParent"      : "#sp-content",
		        "showMenuBtnText"       : "yes",
		        "menuBtnText"           : "PAGES",
		        "siteID"                : "4"
		});

		$("body").csRsSameHeights({
		        resizeElements : [
		                {
		                        "resizeMethod"          : "columns",            // OPTIONS - columns, apps
		                        "parent"                : "#hp-content-lower",        // PARENT THAT COLUMNS AND/OR APPS ARE IN THAT ARE GETTING RESIZED
		                        "columns"               : ".ui-widget.app.headlines li",         // COLUMNS AND/OR APPS WITHIN THESE COLUMNS THAT SAME HEIGHTS IS BEING APPLIED TO -- NEEDS TO BE A CLASS
		                        "gutter"                : 10,                   // PIXEL SPACING BETWEEN APPS -- WILL BE ADDED AS BOTTOM MARGIN
		                        "applySameHeights"      : ["768", "640"]        // OPTIONS - ["960", "768", "640", "480", "320"]
		                }
		        ]
		});

		pgScreenSize();
		useChannelForSection();
		dynamicContent();	
		linkTargets();
        stickyCalendarFix();
	});	

	function pgScreenSize(){
		var getWidth = $(window).width();
		if (getWidth >= 1024) { // 960+
			photoGallery();
			$(window).scroll(parallaxScroll);
			headlinesMod();
		} if (getWidth >= 768 && getWidth < 1024) { // 768+
			photoGallery();
			appView();
		} if (getWidth >= 640 && getWidth < 768) { // 640+
			photoGallery();
			appView();
		} if (getWidth >= 480 && getWidth < 640) { // 480+
			photoGallery();
			appView480();
		} if (getWidth >= 320 && getWidth < 480) { // 320+
			photoGallery();
			appView480();
		}
	}

	function photoGallery(){
		if(!$("#sw-content-container10.region.ui-hp .rs-gallery-container").length){
		        // MULTIMEDIA ROTATOR
		        $("#sw-content-container10.ui-hp div.ui-widget.app.cs-rs-multimedia-rotator").csRsMultimediaRotator({
		                "imageWidth"              : 1400,
		                "imageHeight"             : 590,
		                "appHeader"               : "no",
		                "titles"                  : "yes",
		                "captions"                : "yes",
		                "galleryOverlay"          : "no",
		                "linkedElement"           : ["image"], // ["image", "title", "overlay", "all"]
		                "playPause"               : "no",
		                "mainControls"            : "no",
		                "contMoreLink"            : "no",
		                "contMoreLinkText"        : "Read More",
		                "descMoreLink"            : "no",
		                "descMoreLinkText"        : "Read More",
		                "bullets"                 : "yes",
		                "bulletControls"          : "no",
		                "thumbnails"              : "no",
		                "thumbnailWidth"          : 122,
		                "thumbnailHeight"         : 53,
		                "thumbnailSlideDirection" : "horizontal", // horizontal or vertical
		                "hoverPause"              : "yes",
		                "autoRotate"              : "yes",
		                "fullScreenRotator"       : "no", // image dimensions work well at 1500x915
		                "fullScreenVideo"         : "no",
		                "transitionType"          : "fade", // fade or slide
		                "transitionSpeed"         : 3,
		                "transitionDelay"         : 8,
		                "onImageLoad"             : function() { },
		                "allImagesLoaded"         : function() { },
		                "onThumbnailLoad"         : function() { },
		                "allThumbnailsLoaded"     : function() { },
		                "onTransitionStart"       : function() { },
		                "onTransitionEnd"         : function() { },
		                "watchVidClick"           : function() { },
		                "closeVidClick"           : function() { },
		                "allLoaded"               : function() { 
		                				var getWidth = $(window).width();
		                				if (getWidth >= 1024){
			                				var bulletHeight = $("#hp-slideshow .rs-gallery-container .rs-gallery-bullets").height();
			                				var bulletOffset = -(bulletHeight/2);
			                				$("#hp-slideshow .rs-gallery-container .rs-gallery-bullets-outer").css("margin-top",bulletOffset);

			                				$(window).resize(function(){
										var bulletHeight = $("#hp-slideshow .rs-gallery-container .rs-gallery-bullets").height();
			                					var bulletOffset = -(bulletHeight/2);
			                					$("#hp-slideshow .rs-gallery-container .rs-gallery-bullets-outer").css("margin-top",bulletOffset);
									});
								}		
		                }
		        });
		}
	}

	function appView(){
		//DOC make homepage apps collapsible for small mobile devices
		if(!$("div.ui-widget.app.active").length){
			$(".hp.column.one div.ui-widget.app:first").addClass("active");
		}
		$("div.ui-widget.app").each(function() {
			if($(".ui-widget-header", this).hasClass("ui-helper-hidden") || jQuery.trim($(".ui-widget-header h1", this).text()) == "") {
				$(this).addClass("no-header");
			}
		});


		$("#hp-content-main div.ui-widget-header").click(function(){
			var clickedApp = $(this).parent().parent().attr("id");

			$("body").csRsSameHeights({
			        resizeElements : [
			                {
			                        "resizeMethod"          : "columns",            // OPTIONS - columns, apps
			                        "parent"                : "#hp-content-lower",        // PARENT THAT COLUMNS AND/OR APPS ARE IN THAT ARE GETTING RESIZED
			                        "columns"               : " .ui-widget.app.headlines li",         // COLUMNS AND/OR APPS WITHIN THESE COLUMNS THAT SAME HEIGHTS IS BEING APPLIED TO -- NEEDS TO BE A CLASS
			                        "gutter"                : 10,                   // PIXEL SPACING BETWEEN APPS -- WILL BE ADDED AS BOTTOM MARGIN
			                        "applySameHeights"      : ["480"]        // OPTIONS - ["960", "768", "640", "480", "320"]
			                }
			        ]
			});

			$("div.ui-widget.app").removeClass("active");
			$(this).parent().addClass("active");



			$("html, body").animate({
				scrollTop: $("#" + clickedApp).offset().top
			}, 500, function(){
				$("html, body").stop(true, false);
			});

		});
	}
	function appView480(){
		//DOC make homepage apps collapsible for small mobile devices
		if(!$("div.ui-widget.app.active").length){
			$(".hp.column.one div.ui-widget.app:first").addClass("active");
		}
		$("div.ui-widget.app").each(function() {
			if($(".ui-widget-header", this).hasClass("ui-helper-hidden") || jQuery.trim($(".ui-widget-header h1", this).text()) == "") {
				$(this).addClass("no-header");
			}
		});
		$("div.ui-widget-header").click(function(){

			var clickedApp = $(this).parent().parent().attr("id");

			$("div.ui-widget.app").removeClass("active");
			$(this).parent().addClass("active");

			$("html, body").animate({
				scrollTop: $("#" + clickedApp).offset().top
			}, 500, function(){
				$("html, body").stop(true, false);
			});
		});
	}

	function useChannelForSection(){
		//DOC use channel name for pagelistnavigation header if one is not present
		if(!$("div.sp.column.one .ui-widget-header h1").length > 0) {
			$("div.sp.column.one .ui-widget-header").append("
Students & Families");
		}
        }

	function dynamicContent(){
		// ********** MYSTART ********** //
		//DOC replace mystart dropdown arrows
		$(".sw-myaccount-chevron").text("+");
		$(".sw-dropdown-arrow").text("+");

		$("#sw-mystart-search").appendTo("#gb-search");
		$("#sw-mystart-search #sw-search-button span").addClass("icon-font").text("s");

		$("#sw-mystart-right .sw-mystart-button.signin").clone().appendTo("#gb-mystart-right");
		$("#sw-mystart-right .sw-mystart-button.register").clone().appendTo("#gb-mystart-right");

        $('#ui-btn-signin a span').each(function() {
          var text = $(this).text();
          $(this).text(text.toUpperCase().replace('SIGN IN', 'Staff Login'));   
          $('#ui-btn-signin a').prop('title', 'Staff Login');
        });




		//SEARCH TEXT SPACE FIX
		$("#sw-mystart-search").unbind()

		// ********** HEADER ********** //
		$("#gb-global-icons .icn .icon-text").each(function(){
			var iconTextWidth = $(this).width();
			var textOffset = -(iconTextWidth/2);
			$(this).css("left",textOffset);
		});


		$("#hp-social .icn .icon-text").each(function(){
			var iconTextWidth = $(this).width();
			var textOffset = -(iconTextWidth/2) + 7;
			$(this).css("left",textOffset);
		});


		$(".icn").hover(function(){
			$(".icon-text",this).css("display","block");
		}, function(){
			$(".icon-text",this).css("display","none");
		});

		// ********** CHANNEL BAR ********** //
		//DOC add first class to first channel item
		$("li.sw-channel-item").first().addClass("first");
		//DOC add last class to last channel item
		$("li.sw-channel-item").last().addClass("last");

		$("ul.sw-channel-list li.sw-channel-item").each(function(){
			var channelWidth = $(this).width();
			var dropdownOffset = -(300 - channelWidth)/2;
			$("ul.sw-channel-dropdown",this).css("left",dropdownOffset);
		});


			// Offset of first element
			var first = $("ul.sw-channel-list li.sw-channel-item:first").offset().left;
			var offset = 0;
			var count = 0;

			$("ul.sw-channel-list li.sw-channel-item").each(function () {

			   // Get offset            
			   offset = $(this).offset().left;

			   // If not first box and offset is equal to first box offset
			   if(count > 0 && offset == first)
			   {
			      // Show where break is
			  console.log("Breaks on element: " + count);
			  console.log("Offset is: " + offset);
			  $("ul.sw-channel-dropdown", this).addClass("wrap");
			  $("li.sw-channel-item.last .sw-channel-dropdown").addClass("wrap");
			   }

			   // Next box
			   count++;
			});



		// ********** BODY ********** //
		//DOC rename first breadcrumb
		$("li.ui-breadcrumb-first > a > span").text("Home");
		//DOC replace more-link text
		$("a.more-link span").text("View All");
		//DOC auto-focus username signin field
		$("#swsignin-txt-username").focus();
		//DOC center signed out message and signin button
        	if($("div.ui-spn > div > p > span").text() == "You have been signed out."){
        		$("div.ui-spn > div").css({"text-align" : "center", "padding" : "50px 0px 50px 0px"});
        		//DOC add signed out breadcrumb
        		$(".ui-breadcrumbs").append("
Signed Out");
        	}
        	//DOC add read full story button to each headline
        	//$(".ui-widget.app.headlines").each(function(){
        	//	$(".ui-article", this).each(function(){
        	//		var headlineURL = $("h1 a", this).attr("href");
        	//		$(".ui-article-description", this).append('Read Full Story »');
        	//	});
        	//});
        	//DOC add sign in breadcrumb
        	if($("#swlogin").length){
        		$(".ui-breadcrumbs").append("
Sign In");
        	}
        	//MOD EVENTS
		$(".upcomingevents").modEvents({
			columns:"yes",
			monthLong:"no"
		});
		eventsByDay(".upcomingevents .ui-articles");
		$("div.ui-widget.app.upcomingevents div.upcoming-column-container").each(function(){
		$(".ui-article-description:eq(0)",this).addClass("first");
		});

		//ADD CLASS TO ALL THE ODD LI IN UPCOMING EVENTS FOR ZEBRA STRIPE STYLING
		$(".ui-widget.app.upcomingevents .ui-widget-detail .ui-articles li:even").addClass("odd");

		//CLICK FUNCTION TO SLIDE UP OR DOWN THE REGISTRATION LINKS
		$("#hp-registration-links > ul > li").click(function(){
			$("#hp-registration-links .hp-registration-dropdown").slideToggle("slow");
			if(!$(this).hasClass("active")){
				$(this).addClass("active");
			} else {
				$(this).removeClass("active");
			}
		});

		// APPLY RESPONSIVE DIMENSIONS TO HEADLINE IMAGES
		$("#hp-infographic .graphic img").each(function() {
			$(this).css({"display": "inline-block", "width": "100%", "max-width": $(this).attr("width") + "px", "height": "auto", "max-height": $(this).attr("height") + "px"});
		});

		// APPLY RESPONSIVE DIMENSIONS TO HEADLINE IMAGES
		$("#hp-content-lower .ui-widget.app.headlines img").each(function() {
			$(this).css({"display": "inline-block", "width": "100%", "max-width": $(this).attr("width") + "px", "height": "auto", "max-height": $(this).attr("height") + "px"});
		});

		$("#hp-content-lower .ui-widget.app.headlines li:nth-child(4n)").each(function() {
		$(this).addClass("no-pad");
		});

		//CLICK FUNCTION TO SLIDE UP OR DOWN THE POPULAR LINKS ON SUBPAGES
		$("#sp-pop-links-header h1").click(function(){
			$(".sp #hp-content-upper-outer").slideToggle("slow");
		});

		//TOGGLE THE POPULAR LINKS HEADER TEXT
		$("#sp-pop-links-header h1").toggle(function(){
			$(this).text('- Close Popular Links');
		}, function () {
			$(this).text('+ Expand to view popular links');
		});

		$("#hp-photo-scroll").click(function(){
				var channelHeight = $("#gb-channel-list-outer.fixed").height();
				$("html, body").animate({
					scrollTop: ($("#hp-content-main-outer").offset().top) - channelHeight
				}, 500, function(){
					$("html, body").stop(true, false);
			});  
		});	

		// ********** FOOTER ********** //
		//DOC get Schoolwires footer links
		var feedbackURL = $("div#sw-footer-links li:eq(0) a").attr("href");
		var termsURL = $("div#sw-footer-links li:eq(2) a").attr("href");
		var policyURL = $("div#sw-footer-links li:eq(1) a").attr("href");

		$("a#feedback").attr("href", feedbackURL);
		$("a#terms").attr("href", termsURL);
		$("a#policy").attr("href", policyURL);	

		$("#back-to-top, #back-to-top-resp").click(function(e){
        		e.preventDefault();
        		$("html, body").animate({
				scrollTop: $("body").offset().top
			}, 500, function(){
				$("html, body").stop(true, false);
			});
        	});		
	}

	function headlinesMod(){
		//WRAP THE HEADLINES TITLE AND CAPTION IN A DIV FOR STYLING
		$('#hp-content-lower .ui-widget.app.headlines .ui-widget-detail .ui-articles .ui-article').each(function(){
			if($("a.ui-article-thumb img",this).attr("src")){
				var linkHREF = $("p.ui-article-description",this).text();
				if(!linkHREF == "") {
					$("a.ui-article-thumb", this).attr("href",linkHREF);
					$("h1.ui-article-title a", this).attr("href",linkHREF);
				    	$("h1.ui-article-title", this).next('p.ui-article-description').andSelf().wrapAll('

');
				} else {
					$("h1.ui-article-title", this).next('p.ui-article-description').andSelf().wrapAll('

');
					$("a.ui-article-thumb", this).removeAttr("href").removeClass("ui-article-thumb").css("cursor","default");
					$("a.ui-article-thumb", this).on("click", function (e) {
						e.preventDefault();
					});
					$(".ui-article-title a", this).removeAttr("href").css("cursor","default");
					$(".ui-article-title a", this).on("click", function (e) {
						e.preventDefault();
					});
				}
			}	
		});

		//REMOVE HIDDEN CLASS ON HEADLINES WRAPPER TO SHOW TITLE
		$("#hp-content-lower .ui-widget.app.headlines .ui-article").hover(
			function() {
				if($(".ui-article-inner.has-thumb", this).hasClass("hidden")){
					$(".ui-article-inner.has-thumb", this).removeClass("hidden");
				} else {
					$(".ui-article-inner.has-thumb", this).addClass("hidden");
				}
			}, function() {
				$(".ui-article-inner.has-thumb", this).addClass("hidden");
			}
		);
	}

    function stickyCalendarFix() {
        var channelHeight = $("#gb-channel-list-outer").height();

        if ($(window).scrollTop() <= channelHeight) {

            if($(".sp").length && $(".ui-widget.app.calendar").length) {
                $(".wcm-controls").css("margin-top", "0px");
            }
        } else {

            if($(".sp").length && $(".ui-widget.app.calendar").length) {
                if($(".wcm-controls").hasClass("wcm-stuck")) {
                    $(".wcm-controls").css("margin-top", channelHeight + "px");
                }
            }
        }
    }

	function searchText() {
		//DOC custom mystart search placeholder
	        //SEARCH TEXT
	        $("div#sw-mystart-search input#sw-search-input").removeAttr("placeholder");
		$("div#sw-mystart-search input#sw-search-input").attr("value", "Search This Site");
		$("div#sw-mystart-search input#sw-search-input").focus(function() {
			if($(this).val() == "Search This Site") {
				$(this).val("");
			}
		});
		$("div#sw-mystart-search input#sw-search-input").blur(function() {
			if($(this).val() == "") {
				$(this).val("Search This Site");
			}
		});
	}

	function eventsByDay(container) {
		//DOC move all events with the same dates into the same container
		var countLi = 0;
		$(".ui-article", container).each(function(){
			if (!$(this).find("h1.ui-article-title.sw-calendar-block-date").size()){
				var moveArticle = $(this).html();
				$(this).parent().prev().children().children().next().append(moveArticle);
				$(this).parent().remove();

			};
		});
	}

	function calendarListView(){
		//DOC auto click function for schoolwires calendar list view for small mobile devices
		if(!$("a.ui-btn-toggle.list").hasClass("active")){
			$("a.ui-btn-toggle.list").click();
		}
	}

	var parallaxScroll = function(){
		var getWidth = $(window).width();
		var mystartHeight = $("#gb-mystart-outer").height();

		if (getWidth >= 1024) { // 960+
			if($(window).scrollTop() >= mystartHeight) {
				$("#gb-channel-list-outer").addClass("fixed");
				$("#gb-header-outer-fixed").removeClass("hidden");
				$("#gb-header-outer").addClass("hidden");
				if ($("#hp-alert-outer .ui-widget.app .ui-widget-detail li").length) {
					$("#hp-alert-outer").css("margin-top","115px");
				} else {
					$("#hp-slideshow-outer").css("margin-top","115px");
					$("#sp-pop-links-header-outer").css("margin-top","115px");
				}
				$("#gb-global-icons-fixed .icn .icon-text").each(function(){
						var fixedIconTextWidth = $(this).width();
						var textOffset = -(fixedIconTextWidth/2);
						$(this).css("left",textOffset);
				});

			} else {
				$("#gb-channel-list-outer").removeClass("fixed");
				$("#gb-header-outer").removeClass("hidden");
				$("#gb-header-outer-fixed").addClass("hidden");
				if ($("#hp-alert-outer .ui-widget.app .ui-widget-detail li").length) {
					$("#hp-alert-outer").css("margin-top","0px");
				}
				else {
					$("#hp-slideshow-outer").css("margin-top","0px");
					$("#sp-pop-links-header-outer").css("margin-top","0px");
				}
			}
		}	
	}

	function linkTargets(){
		        var globalIconLink1Target = 'Same Window';
		        var globalIconLink2Target = 'Same Window';
		        var globalIconLink3Target = 'New Window';
		        var globalIconLink4Target = 'Same Window';
		        var globalIconLink5Target = 'New Window';
                var globalIconLink6Target = 'Same Window';
		        if(globalIconLink1Target == "New Window"){
		        	globalIconLink1Target = "_blank";
		        } else {
		        	globalIconLink1Target = "_self";
		        }
		        if(globalIconLink2Target == "New Window"){
		        	globalIconLink2Target = "_blank";
		        } else {
		        	globalIconLink2Target = "_self";
		        }
		        if(globalIconLink3Target == "New Window"){
		        	globalIconLink3Target = "_blank";
		        } else {
		        	globalIconLink3Target = "_self";
		        }
		        if(globalIconLink4Target == "New Window"){
		        	globalIconLink4Target = "_blank";
		        } else {
		        	globalIconLink4Target = "_self";
		        }
		        if(globalIconLink5Target == "New Window"){
		        	globalIconLink5Target = "_blank";
		        } else {
		        	globalIconLink5Target = "_self";
		        }
                if(globalIconLink6Target == "New Window"){
		        	globalIconLink6Target = "_blank";
		        } else {
		        	globalIconLink6Target = "_self";
		        }
		        $("#gb-global-icons .icn.one").attr("target", globalIconLink1Target);
		        $("#gb-global-icons .icn.two").attr("target", globalIconLink2Target);
		        $("#gb-global-icons .icn.three").attr("target", globalIconLink3Target);
		        $("#gb-global-icons .icn.four").attr("target", globalIconLink4Target);
		        $("#gb-global-icons .icn.five").attr("target", globalIconLink5Target);
                $("#gb-global-icons .icn.six").attr("target", globalIconLink6Target);
		        $("#gb-global-icons-fixed .icn.one").attr("target", globalIconLink1Target);
		        $("#gb-global-icons-fixed .icn.two").attr("target", globalIconLink2Target);
		        $("#gb-global-icons-fixed .icn.three").attr("target", globalIconLink3Target);
		        $("#gb-global-icons-fixed .icn.four").attr("target", globalIconLink4Target);
		        $("#gb-global-icons-fixed .icn.five").attr("target", globalIconLink5Target);
                $("#gb-global-icons-fixed .icn.six").attr("target", globalIconLink6Target);

		        var popularLink1Target = 'Same Window';
		        var popularLink2Target = 'Same Window';
		        var popularLink3Target = 'Same Window';
		        var popularLink4Target = 'Same Window';
		        var popularLink5Target = 'Same Window';
		        var popularLink6Target = 'Same Window';
		        if(popularLink1Target == "New Window"){
		        	popularLink1Target = "_blank";
		        } else {
		        	popularLink1Target = "_self";
		        }
		        if(popularLink2Target == "New Window"){
		        	popularLink2Target = "_blank";
		        } else {
		        	popularLink2Target = "_self";
		        }
		        if(popularLink3Target == "New Window"){
		        	popularLink3Target = "_blank";
		        } else {
		        	popularLink3Target = "_self";
		        }
		        if(popularLink4Target == "New Window"){
		        	popularLink4Target = "_blank";
		        } else {
		        	popularLink4Target = "_self";
		        }
		        if(popularLink5Target == "New Window"){
		        	popularLink5Target = "_blank";
		        } else {
		        	popularLink5Target = "_self";
		        }
		        if(popularLink6Target == "New Window"){
		        	popularLink6Target = "_blank";
		        } else {
		        	popularLink6Target = "_self";
		        }
		        $("#hp-content-upper .poplink.one").attr("target", popularLink1Target);
		        $("#hp-content-upper .poplink.two").attr("target", popularLink2Target);
		        $("#hp-content-upper .poplink.three").attr("target", popularLink3Target);
		        $("#hp-content-upper .poplink.four").attr("target", popularLink4Target);
		        $("#hp-content-upper .poplink.five").attr("target", popularLink5Target);
		        $("#hp-content-upper .poplink.six").attr("target", popularLink6Target);
		}

		function googleTranslateElementInit() {
		 new google.translate.TranslateElement({pageLanguage: 'en'}, 'google_translate_element');
		 var translator = $("#google_translate_element > div > div:eq(0)");
		 $("#google_translate_element").html(translator);
	}

