            }

            function showModal(url, wnd, width, height) {
                var scheme = window.location.href.split('/')[0] || 'http:';
                radopen(scheme === 'https:' || $('.secureform').length > 0
                    ? setPreventHttpRedirectParam(url) : url, wnd, width + 'px', height + 'px').center();
            }

