$(document).ready(function(){
   var beaconURL='https://analytics.schoolwires.com/analytics.asmx/Insert?AccountNumber=lQPNy3xVRj5ri1KqUvUbpw%3d%3d&SessionID=d17dcbf1-86e4-4750-bebc-5496c7769651&SiteID=4&ChannelID=18&SectionID=467&PageID=1820&HitDate=4%2f20%2f2020+6%3a23%3a17+PM&Browser=Unknown+0.0&OS=Unknown&IPAddress=10.61.17.116';
