div.script:
function handleSelect(obj) {
    var option = $(obj).find("option:selected");
    if ($(option).attr("target") == undefined) {
        window.location = $(option).attr("href");
    } else {
        window.open($(option).attr("href"), $(option).attr("target"));
    }
}

footer.script:
    /*  */
    function processForm() {
        var txtEmail = document.getElementById('newsletter-input');
        if (txtEmail.value.length == 0 || txtEmail.value == "") {
            alert("Please enter an email address.");
        }else {
            window.open("https://feedburner.google.com/fb/a/mailverify?loc=en_US&uri=blogspot/AjNcR&email="+txtEmail.value +"", "popupwindow", "scrollbars=yes,width=550,height=520");
            return true;
        }
    }
