	function hpAppBottoms(){

        	//IF THERE ARE NO APPS IN COLUMN 2 OTHER THAN THE TABBED BLOCKS, ADD THE LAST CLASS TO EACH OF THE TABBED APPS
        	if(!$("div#sw-content-container3 div.ui-widget.app").length){
        		$("div.hp.column.one div.ui-widget.app:last, div.hp.column.three div.ui-widget.app:last").addClass("last");
        		$("div.hp.column.two div.ui-widget.app").each(function(){
        			$(this).addClass("last");
        		});
        		$("div.ui-widget.app.flexpage div.clear").remove();
        		$("div.hp.column.one div.ui-widget.app:last").removeClass("flexpage").addClass("navigation");

        	} else {
        	//GIVE THE LAST APP IN EACH HP COLUMN THE LAST CLASS TO LINEUP BOTTOM APPS
	        	$("div.hp.column").each(function(){
	        		$("div.ui-widget.app:last", this).addClass("last");
	        		$("div.ui-widget.app.flexpage div.clear").remove();

	        	});
	        }
	}
	function dynamicContent(){

        //Added by Lyndsay - Case 00366091 
        $("#ui-btn-signin a span").text("Schoolwires Login");

		//Added by Marie - Case #00111033
		if ("npd117" == "oakridge") {
			$("#connect .icn.facebook").css("display","none");
		}
		//Added by Justine - Case #00111311
		if ("npd117" == "npd117") {
			$("#connect .icn.facebook").css("display","none");
		}
		//TAGLINE
		$("div#hp-tagline, div#sp-tagline").html(taglineText);

		//TABBED APPS
        	$("div#hp-content #sw-content-container2").tabBlocks();
        	$("div.tabs-container").children().wrapAll("
");

        	//COLOR CHANGES
        	$("body").css("background-color", primaryColor);
        	$("#sw-channel-list-container, div#tabs-container-inner, div#sw-content-container3 div.ui-widget.app div.ui-widget-header").css("background-color", secondaryColor);
        	$("div#hp-tagline, div#sp-tagline").css("color", taglineColor);
        	$("div#hp-tabs, div.tabs-container").css("background-color", primaryColor);

        	// TABBED APPS ROUNDED CORNERS
        	$("div#hp-content div#sw-content-container3 div.ui-widget.app div.ui-widget-header").corner("top 6px");
        	$("div.tabs-container").corner("top 6px");

        	//SEARCH TEXT
        	$("div#sw-mystart-search input#sw-search-input").attr("value", "Search...");
		$("div#sw-mystart-search input#sw-search-input").focus(function() {
			if($(this).val() == "Search...") {
				$(this).val("");
			}
		});
		$("div#sw-mystart-search input#sw-search-input").blur(function() {
			if($(this).val() == "") {
				$(this).val("Search...");
			}
		});
        }
        function homepageAwards(){
        	if(homepageAward == "yes"){
        		$("div.hp.column.three div#sw-content-container4 div.ui-widget.app:first div.ui-widget-footer").hide();

        			/* Added in the uHeight variable to handle subsite height variances Jeremy - Case #00104384 */
        		var uHeight = "115px";

        		if( "npd117" == "conrady"){
        			uHeight = "130px";
        		}

        		$("div.hp.column.three div#sw-content-container4 div.ui-widget.app:first div.ui-widget-detail").css({
        			"background"	:"none",
        			"width"		:"241px",
        			"height"	:uHeight,
        			"overflow"	:"hidden"
        		});

        			//Added by Jeremy - Case #00104384 
        		if( "npd117" == "conrady" && $('.sp').length > 0 ){ //targets only the subpages for conrady site to alter the header image source
        			$("#sp-logo > a > img").attr("src","/cms/lib/IL01001910/Centricity/template/8/logos/npd117-sp-alt.png");
        		}

        		$("div#homepage-awards").css("background","url(/cms/lib/IL01001910/Centricity/template/8/logos/npd117-sp.png) no-repeat top right transparent");

        		if("npd117" == "npd117"){
        			$("div#homepage-awards").after("
");
        			$("div#homepage-awards").css("right","20px");
        		} if("npd117" == "glenoaks"){
                	//commented out old code for glenoak and hid old background image. re-developed glenoak's homepage awards section with other three lines of code below by Lyndsay - 1.1.16 - Case 00416613 
                	$("div#homepage-awards").css("background", "none");
                	$("div#homepage-awards").html("");
        			//$("div#homepage-awards").after("
");
                	$(".glenoaks div#homepage-awards").css({"top": "35px", "right": "17px"});
                    $(".glenoaks #homepage-awards img").css("width", "75px");
                } else{
        			$("div#homepage-awards").after("
");
        		}	
        		$("div#sw-content-container4").show();
        	} else {
        		$("div#sw-content-container4").show();
        	}
        }
        function useChannelForSection() {
		if(!$("div.sp.column.one .ui-widget-header h1").length > 0) {
			$("div.sp.column.one .ui-widget-header").append("
");
		}
	}
	function loadFlash(){
		if($("#hp-slideshow-overlay").size())
	         {
	            var so = new SWFObject("/cms/lib/IL01001910/Centricity/Template/8/slideshow-overlay.swf","mymovie1", "540", "224", "9", "#FFFFFF");
	            so.addParam("wmode", "transparent");
	            so.addVariable("primaryColor", primaryColor);
	            if(window.location.host == "translate.googleusercontent.com"){
			var locationText = String(window.location);
			locationText = locationText.split("//");
			locationText = locationText[2].split("/");
			so.addVariable("homeURL", locationText[0]);	
		    } else {
			so.addVariable("homeURL", window.location.host);
		    }
	            so.write("hp-slideshow-overlay");
	         }
	         if (!$("#mymovie1").size()){
	          	$("#hp-slideshow-overlay").html("");
	          }
        }
	function dynamicStylesheet(){
		var dynStyleSheet = document.createElement('style');
		if(dynStyleSheet) {
			dynStyleSheet.setAttribute('type', 'text/css');
			var head = document.getElementsByTagName('head')[0];
			if(head) {
	      			head.appendChild(dynStyleSheet);
			}
			var dynStyles = ".sw-mystart-button:hover, #sw-mystart-account:hover, .sw-mystart-dropdown .sw-dropdown-list li:hover {" +
					"	background-color: " + primaryColor + ";" +
					"}";
			var rules = document.createTextNode(dynStyles);
			if(dynStyleSheet.styleSheet){// IE
				dynStyleSheet.styleSheet.cssText = dynStyles;
			} else {
				dynStyleSheet.appendChild(rules);
			}
		}
	}
