	}

	function tabBlock() {
		head.ready("tabBlock", function(){
			$("#sw-content-container2.ui-hp").tabBlocks();
			$("div.tab-button").last().addClass("last");
		});
	}

	function modEvents(){
		head.ready("modEvents", function(){
		    if (!$(".upcomingevents .joel-month").length) {
		        $(".upcomingevents").modEvents({
		            columns     : "yes",
		            monthLong   : "no",
		            dayWeek     : "no"
		        });
		        eventsByDay(".upcomingevents .ui-articles");
		    }
		});
	}

	function eventsByDay(container) {
	    var countLi = 0;
	    $(".ui-article", container).each(function(){
	        if (!$(this).find("h1.ui-article-title.sw-calendar-block-date").size()){
	            var moveArticle = $(this).html();
	            $(this).parent().prev().children().children().next().append(moveArticle);
	            $(this).parent().remove();
	        };
	    });
	}

	function viewFullSite() {
		head.ready("fullSite", function() {
			// HEAD CONTENT
			$("body").viewFullSite({
				"breakPoint"		: 1023, // SYSTEM BREAK POINTS - 1023, 767, 639, 479
				"buttonContainer"	: "body",
				"useDefaultCSS"		: "yes",
				"fixedPosition"		: "bottom" // IF USING DEFAULT STYLES - TOP, BOTTOM
			});
		});
	}	

	function useChannelForSection(){
		//DOC use channel name for pagelistnavigation header if one is not present
		if(!$("div.sp.column.one .ui-widget-header h1").length > 0) {
			$("div.sp.column.one .ui-widget-header").append("
Resources");
		}
        }

	function dynamicContent(){
		// ********** MYSTART ********** //
		//DOC replace mystart dropdown arrows
		$(".sw-dropdown-arrow").addClass("icon-font").text("d");
		// REPLACE SCHOOL LIST TEXT
		$(".sw-mystart-dropdown.schoollist .selector, .sw-mystart-dropdown.schoollist .sw-dropdown-selected").text("Select a School");
		// ADD DISTRICT SITE TO SCHOOL LIST
		$(".sw-mystart-dropdown.schoollist .sw-dropdown-list").prepend("
Orland School District 135");
		// MOVE SCHOOL LIST
		$("#gb-schoollist").append($(".sw-mystart-dropdown.schoollist"));
		//DOC move mystart buttons and dropdowns

			if($(".sw-mystart-button.register").length){
				$("#gb-mystart-btns").append($(".sw-mystart-button.register"));
			}
			$("#gb-mystart-btns").append($(".sw-mystart-button.signin"));



		// ********** HEADER ********** //
		//DOC add logo
		var logoSrc = jQuery.trim("/cms/lib/IL01905133/Centricity/Template/GlobalAssets/images///logos/OrlandSchoolDistWEBSITE.png");
		var srcSplit = logoSrc.split("/");
		var srcSplitLen = srcSplit.length;
		if(logoSrc != "" && srcSplit[srcSplitLen - 1] != "default-man.jpg") {
			$("#gb-logo").html('').removeClass("hidden");
		}

		// ********** CHANNEL BAR ********** //
		//DOC add first class to first channel item
		$("li.sw-channel-item").first().addClass("first");
		//DOC add last class to last channel item
		$("li.sw-channel-item").last().addClass("last");

		// ********** BODY ********** //
		//DOC rename first breadcrumb
		$("li.ui-breadcrumb-first > a > span").text("Home");
		//DOC replace more-link text
		$("a.more-link span").text("View More");
		//DOC auto-focus username signin field
		$("#swsignin-txt-username").focus();
		//DOC center signed out message and signin button
        	if($("div.ui-spn > div > p > span").text() == "You have been signed out."){
        		$("div.ui-spn > div").css({"text-align" : "center", "padding" : "50px 0px 50px 0px"});
        		//DOC add signed out breadcrumb
        		$(".ui-breadcrumbs").append("
Signed Out");
        	}
        	//DOC add sign in breadcrumb
        	if($("#swlogin").length){
        		$(".ui-breadcrumbs").append("
Sign In");
        	}
        	// SOCIAL ICONS
        	var socialIcons = [
        		["weather", "true"],
        		["twitter", "true"],
        		["facebook", "true"],
        		["pinterest", "false"]
        	];
        	for(var i in socialIcons) {
        		if(socialIcons[i][1] == "true") {
        			$(".gb-social-icon." + socialIcons[i][0]).removeClass("hidden");
        		}
        	}
        	// APPLY RESPONSIVE DIMENSIONS TO CONTENT IMAGES
        	$("div.ui-widget.app .ui-widget-detail img")
        	.not($("div.ui-widget.app.cs-rs-multimedia-rotator .ui-widget-detail img"))
        	.not($("div.ui-widget.app.file-library .ui-widget-detail img"))
        	.each(function() {
			$(this).css({"display": "inline-block", "width": "100%", "max-width": $(this).attr("width") + "px", "height": "auto", "max-height": $(this).attr("height") + "px"});
		});

		// ********** FOOTER ********** //
		//DOC get Schoolwires footer links
		$("a#feedback").text($("div#sw-footer-links li:eq(0) a").text()).attr("href", $("div#sw-footer-links li:eq(0) a").attr("href"));
		$("a#terms").text($("div#sw-footer-links li:eq(2) a").text()).attr("href", $("div#sw-footer-links li:eq(2) a").attr("href"));
		$("a#policy").text($("div#sw-footer-links li:eq(1) a").text()).attr("href", $("div#sw-footer-links li:eq(1) a").attr("href"));		
		// GET DATE
		var d = new Date();
		$("#gb-footer-date").html(d.getFullYear());
	}

	function searchText() {
		// OPEN/CLOSE SEARCH BAR
		$("#gb-search-btn").click(function() {
			if($("#gb-search-outer").hasClass("closed")){
				$("#gb-search-outer").slideDown(function() {
					$(this).removeClass("closed").removeAttr("style");
				});
			} else {
				$("#gb-search-outer").slideUp(function() {
					$(this).addClass("closed").removeAttr("style");
				});
			}
		});

		// SUBMIT SEARCH FORM
		$(".gb-search-button").click(function(e){
		    e.preventDefault();
		    var searchForm = $(this).parent();
		    window.location.href = "/site/Default.aspx?PageType=6&SiteID=4&SearchString=" + $(".gb-search-input", searchForm).val();
		});
		$(".gb-search-form").submit(function(e){
		    e.preventDefault();
		    var searchForm = $(this).parent();
		    window.location.href = "/site/Default.aspx?PageType=6&SiteID=4&SearchString=" + $(".gb-search-input", searchForm).val();
		});

		// SEARCH INPUT VALUE
		$(".gb-search-input").focus(function() {
		    if($(this).val() == "What are you searching for?") {
		        $(this).val("");
		    }
		});
		$(".gb-search-input").blur(function() {
		    if($(this).val() == "") {
		        $(this).val("What are you searching for?");
		    }
		});
	}

	function calendarListView(){
		//DOC auto click function for schoolwires calendar list view for small mobile devices
		if(!$(".ui-widget.app.calendar #calendarlist-pnl-specialview.loaded").length){
			$("a.ui-btn-toggle.list").click();
			$("a.ui-btn-toggle.month").click();
			$(".ui-widget.app.calendar #calendarlist-pnl-specialview").addClass("loaded");
		}

		var getWidth = jsBreakPoint();
		if(getWidth >= 640){ // 640+
			$(".ui-widget.app.calendar #calendar").removeClass("ui-helper-hidden");
			$(".ui-widget.app.calendar #calendarlist-pnl-specialview").addClass("ui-helper-hidden");
		} if(getWidth < 640){ // 480-
			$(".ui-widget.app.calendar #calendar").addClass("ui-helper-hidden");
			$(".ui-widget.app.calendar #calendarlist-pnl-specialview").removeClass("ui-helper-hidden");
			$(".ui-btn-toggle.month").removeClass("active");
			$(".ui-btn-toggle.list").addClass("active");
		}
	}

	function jsBreakPoint(){
		// 960
		if ($(window).width() >= 1024) { return 960; } 

		// 768
		if ($(window).width() >= 768 && $(window).width() < 1024) { return 768; }

		// 640
		if ($(window).width() >= 640 && $(window).width() < 768) { return 640; }

		// 480
		if ($(window).width() >= 480 && $(window).width() < 640) { return 480; }

		// 320
		if ($(window).width() >= 320 && $(window).width() < 480) { return 320; }
	}

