    AllyClientID = "599";
	    // Wait for the API to load     
	    ally.ready(function() {
		    // Configure an API instance
            const ui = ally.ui({
			    'client': {
				    'baseUrl': 'https://prod.ally.ac',
				    'clientId': 599
			    },
			    'courseId': '1443',
			    'locale': $('html').attr('lang'),
			    'platformName': 'Wcm',  
			    'role': 'anonymous'
		    });
	        // Apply the API to the page body   
            ui.autoUpdate({'domWatch': 250});
    });
