link.script:
window.dataLayer = window.dataLayer || [];
function gtag(){dataLayer.push(arguments);}
gtag('js', new Date());

gtag('config', 'UA-123206398-1');


    $(function() {
        CreativeTemplate.Init();
    });

    var CreativeTemplate = {
        // PROPERTIES
        "KeyCodes": { "tab": 9, "enter": 13, "esc": 27, "space": 32, "end": 35, "home": 36, "left": 37, "up": 38, "right": 39, "down": 40 },
        "IsMyViewPage": false, // UPDATES IN SetTemplateProps METHOD
        "ShowDistrictHome": true,
        "ShowSchoolList": false, // UPDATES IN SetTemplateProps METHOD
        "ShowTranslate": true,

        // METHODS
        "Init": function() {
            // FOR SCOPE
            var _this = this;

            this.SetTemplateProps();
            this.MyStart();
            this.JsMediaQueries();
            this.SchoolList();
            this.Translate();
            this.Header();
            this.Search();
            this.RsMenu();
            this.ChannelBar();
            this.StickyChannelBar();
            this.StreamingVideo();
            this.Body();
            this.Homepage();
            this.ModEvents();
            this.GlobalIcons();
            this.AppAccordion();
            this.Footer();
            csGlobalJs.OpenInNewWindowWarning();

            $(window).load(function() { _this.WindowLoad(); });
            $(window).resize(function() { _this.WindowResize(); });
            $(window).scroll(function() { _this.WindowScroll(); });
        },

        "SetTemplateProps": function() {
            // MYVIEW PAGE CHECK
            if($("#pw-body").length) this.IsMyViewPage = true;

            // SCHOOL LIST CHECK
            if($(".sw-mystart-dropdown.schoollist").length) this.ShowSchoolList = true;
        },

        "WindowLoad": function() {

        },

        "WindowResize": function() {
            this.JsMediaQueries();
        },

        "WindowScroll": function() {
            this.Parallax();
        },

        "JsMediaQueries": function() {
            switch(this.GetBreakPoint()) {
                case "desktop": 
                    $("#hp-video").height($(window).height());
                break;
                case "768":
                    $("#hp-video-wrapper").height(493);
                break;
                case "640":
                    $("#hp-video-wrapper").height(406);
                break;
                case "480":
                    $("#hp-video-wrapper").height(295);
                break;
                case "320":
                    $("#hp-video-wrapper").height(245);
                break;
            }
        },

        "Parallax": function(){
        	// FOR SCOPE
            var _this = this;

            var scrolled = $(window).scrollTop();

            if($("#gb-page").hasClass("hp")){
            	if(this.GetBreakPoint() == "desktop"){
                	if(scrolled >= 469){
                        $(".hp-row.one div.ui-widget.app div.ui-widget-header h1").css('top', "13.1px").addClass("leaves");    
                    } else {
                        $(".hp-row.one div.ui-widget.app div.ui-widget-header h1").css('top',-((scrolled*0.1)-60)+'px');
                    }
                } else {
                	 if(scrolled >= 133){
                        $(".hp-row.one div.ui-widget.app div.ui-widget-header h1").css('top', "55.7px");
                    } else {
                        $(".hp-row.one div.ui-widget.app div.ui-widget-header h1").css('top',-((scrolled*0.1)-69)+'px'); 
                    } 
                }   

                $(".plx").each(function(){
                    if($(this).hasClass("nowucme")){
                        var firstTop = $(this).offset().top;
                        var winScrollTop = $(window).scrollTop();
                        var shiftDistance = (firstTop - winScrollTop) *0.1;

                        $(this).css("top",shiftDistance);
                    }

                })
            }

            $(".plx-two").each(function(){
                if($(this).hasClass("nowucme")){
                    var firstTop = $(this).offset().top;
                    var winScrollTop = $(window).scrollTop();
                    var shiftDistance = ((firstTop - winScrollTop) *0.1) - 50;

                    $(this).css("top",shiftDistance);
                }
            }) 

            $(".plx-three").each(function(){
                if($(this).hasClass("nowucme")){
                    var firstTop = $(this).offset().top;
                    var winScrollTop = $(window).scrollTop();
                    var shiftDistance = ((firstTop - winScrollTop) *0.1) - 25;

                    $(this).css("top",shiftDistance);
                }
            })
        },

        "MyStart": function() {
            // FOR SCOPE
            var _this = this;

            // BUILD USER OPTIONS DROPDOWN
            var userOptionsItems = "";



            // SIGNIN BUTTON
            if($(".sw-mystart-button.signin").length) {
                $(".cs-mystart-button.guest.signin").show();
            }

            // REGISTER BUTTON
            if($(".sw-mystart-button.register").length) {
                $(".cs-mystart-button.guest.register").show();
            }



            // BIND DROPDOWN EVENTS
            this.DropdownActions({
                "dropdownParent": ".cs-mystart-dropdown.user-options",
                "dropdownSelector": ".cs-selector",
                "dropdown": ".cs-dropdown",
                "dropdownList": ".cs-dropdown-list"
            });
        },

        "SchoolList": function() {
            // ADD SCHOOL LIST
            if(this.ShowSchoolList) {
                var schoolDropdown =    '
' +
                                            '
Our Schools' +
                                            '
' +
                                                '' + $(".sw-mystart-dropdown.schoollist .sw-dropdown-list").html() + '' +
                                            '' +
                                        '';

                // ADD SCHOOL LIST TO THE DOM   
                if($("#gb-page.hp").length){
                    $("#gb-mystart").append(schoolDropdown);
                } else {
                    $(".cs-mystart-dropdown.translate").before(schoolDropdown);
                }

                // BIND DROPDOWN EVENTS
                this.DropdownActions({
                    "dropdownParent": ".cs-mystart-dropdown.schools",
                    "dropdownSelector": ".cs-selector",
                    "dropdown": ".cs-dropdown",
                    "dropdownList": ".cs-dropdown-list"
                });
            }
        },

        "Translate": function() {
            // ADD TRANSLATE
            if(this.ShowTranslate) {
                $(".cs-mystart-dropdown.translate .cs-dropdown").creativeTranslate({
                    "type": 2, // 1 = FRAMESET, 2 = BRANDED, 3 = API
                    "languages": [ // ["ENGLISH LANGUAGE NAME", "TRANSLATED LANGUAGE NAME", "LANGUAGE CODE"]
                        ["Afrikaans", "Afrikaans", "af"],
                        ["Albanian", "shqiptar", "sq"],
                        ["Amharic", "አማርኛ", "am"],
                        ["Arabic", "العربية", "ar"],
                        ["Armenian", "հայերեն", "hy"],
                        ["Azerbaijani", "Azərbaycan", "az"],
                        ["Basque", "Euskal", "eu"],
                        ["Belarusian", "Беларуская", "be"],
                        ["Bengali", "বাঙালি", "bn"],
                        ["Bosnian", "bosanski", "bs"],
                        ["Bulgarian", "български", "bg"],
                        ["Burmese", "မြန်မာ", "my"],
                        ["Catalan", "català", "ca"],
                        ["Cebuano", "Cebuano", "ceb"],
                        ["Chichewa", "Chichewa", "ny"],
                        ["Chinese Simplified", "简体中文", "zh-CN"],
                        ["Chinese Traditional", "中國傳統的", "zh-TW"],
                        ["Corsican", "Corsu", "co"],
                        ["Croatian", "hrvatski", "hr"],
                        ["Czech", "čeština", "cs"],
                        ["Danish", "dansk", "da"],
                        ["Dutch", "Nederlands", "nl"],
                        ["Esperanto", "esperanto", "eo"],
                        ["Estonian", "eesti", "et"],
                        ["Filipino", "Pilipino", "tl"],
                        ["Finnish", "suomalainen", "fi"],
                        ["French", "français", "fr"],
                        ["Galician", "galego", "gl"],
