        "DropdownActions": function(params) {
            // FOR SCOPE
            var template = this;

            var dropdownParent = params.dropdownParent;
            var dropdownSelector = params.dropdownSelector;
            var dropdown = params.dropdown;
            var dropdownList = params.dropdownList;

            $(dropdownParent + " " + dropdownList + " a").attr("tabindex", "-1");

            // MYSTART DROPDOWN SELECTOR CLICK EVENT
            $(dropdownParent).on("click", dropdownSelector, function(e) {
                e.preventDefault();

                if($(this).parent().hasClass("open")){
                    $("+ " + dropdownList + " a").attr("tabindex", "-1");
                    $(this).attr("aria-expanded", "false").parent().removeClass("open").find(dropdown).attr("aria-hidden", "true").slideUp(300, "swing"); 
                } else {
                    $(this).attr("aria-expanded", "true").parent().addClass("open").find(dropdown).attr("aria-hidden","false").slideDown(300, "swing");
                }
            });

            // MYSTART DROPDOWN SELECTOR KEYDOWN EVENTS
            $(dropdownParent).on("keydown", dropdownSelector, function(e) {
                // CAPTURE KEY CODE
                switch(e.keyCode) {
                    // CONSUME LEFT AND UP ARROWS
                    case template.KeyCodes.enter:
                    case template.KeyCodes.space:
                        e.preventDefault();

                        // IF THE DROPDOWN IS OPEN, CLOSE IT
                        if($(dropdownParent).hasClass("open")){
                            $("+ " + dropdown + " " + dropdownList + " a").attr("tabindex", "-1");
                            $(this).attr("aria-expanded", "false").parent().removeClass("open").find(dropdown).attr("aria-hidden", "true").slideUp(300, "swing"); 
                        } else {
                            $(this).attr("aria-expanded", "true").parent().addClass("open").find(dropdown).attr("aria-hidden", "false").slideDown(300, "swing", function(){
                                $(dropdownList + " li:first-child a", this).attr("tabindex", "0").focus();
                            });
                        }
                    break;

                    // CONSUME TAB KEY
                    case template.KeyCodes.tab:
                        if($("+ " + dropdown + " " + dropdownList + " a").length) {
                            $("+ " + dropdown + " " + dropdownList + " a").attr("tabindex", "-1");
                            $(this).attr("aria-expanded", "false").parent().removeClass("open").find(dropdown).attr("aria-hidden", "true").slideUp(300, "swing");
                        }
                    break;

                    // CONSUME LEFT AND UP ARROWS
                    case template.KeyCodes.down:
                    case template.KeyCodes.right:
                        e.preventDefault();

                        $("+ " + dropdown + " " + dropdownList + " a").attr("tabindex", "-1");
                        $("+ " + dropdown + " " + dropdownList + " li:first-child > a", this).attr("tabindex", "0").focus();
                    break;
                }
            });

            // MYSTART DROPDOWN LINK KEYDOWN EVENTS
            $(dropdownParent).on("keydown", dropdownList + " li a", function(e) {
                // CAPTURE KEY CODE
                switch(e.keyCode) {
                    // CONSUME LEFT AND UP ARROWS
                    case template.KeyCodes.left:
                    case template.KeyCodes.up:
                        e.preventDefault();

                        // IS FIRST ITEM
                        if($(this).parent().is(":first-child")) {
                            // FOCUS DROPDOWN BUTTON
                            $(this).closest(dropdownList).find("a").attr("tabindex", "-1");
                            $(this).closest(dropdownParent).find(dropdownSelector).focus();
                        } else {
                            // FOCUS PREVIOUS ITEM
                            $(this).closest(dropdownList).find("a").attr("tabindex", "-1");
                            $(this).parent().prev("li").find("> a").attr("tabindex", "0").focus();
                        }
                    break;

                    // CONSUME RIGHT AND DOWN ARROWS
                    case template.KeyCodes.right:
                    case template.KeyCodes.down:
                        e.preventDefault();

                        // IS LAST ITEM
                        if($(this).parent().is(":last-child")) {
                            // FOCUS FIRST ITEM
                            $(this).closest(dropdownList).find("a").attr("tabindex", "-1");
                            $(this).closest(dropdownList).find("li:first-child > a").attr("tabindex", "0").focus();
                        } else {
                            // FOCUS NEXT ITEM
                            $(this).closest(dropdownList).find("a").attr("tabindex", "-1");
                            $(this).parent().next("li").find("> a").attr("tabindex", "0").focus();
                        }
                    break;

                    // CONSUME TAB KEY
                    case template.KeyCodes.tab:
                        if(e.shiftKey) {
                            e.preventDefault();

                            // FOCUS DROPDOWN BUTTON
                            $(this).closest(dropdownList).find("a").attr("tabindex", "-1");
                            $(this).closest(dropdownParent).find(dropdownSelector).focus();
                        }
                    break;

                    // CONSUME HOME KEY
                    case template.KeyCodes.home:
                        e.preventDefault();

                        // FOCUS FIRST ITEM
                        $(this).closest(dropdownList).find("a").attr("tabindex", "-1");
                        $(this).closest(dropdownList).find("li:first-child > a").attr("tabindex", "0").focus();
                    break;

                    // CONSUME END KEY
                    case template.KeyCodes.end:
                        e.preventDefault();

                        // FOCUS LAST ITEM
                        $(this).closest(dropdownList).find("a").attr("tabindex", "-1");
                        $(this).closest(dropdownList).find("li:last-child > a").attr("tabindex", "0").focus();
                    break;
                }
            });

            $(dropdownParent).mouseleave(function() {
                if($(".ff").length){
					if(!$(dropdownParent).hasClass("translate")){
                        $(dropdownList + " a", this).attr("tabindex", "-1");
                        $(dropdownSelector, this).attr("aria-expanded", "false").parent().removeClass("open").find(dropdown).attr("aria-hidden", "true").slideUp(300, "swing"); 
                    } 
                } else {
                	$(dropdownList + " a", this).attr("tabindex", "-1");
                    $(dropdownSelector, this).attr("aria-expanded", "false").parent().removeClass("open").find(dropdown).attr("aria-hidden", "true").slideUp(300, "swing");
                }

            }).focusout(function() {
                var thisDropdown = this;
                if($(".ff").length){
                	if(!$(dropdownParent).hasClass("translate")){
                        setTimeout(function () {
                            if(!$(thisDropdown).find(":focus").length) {
                                $(dropdownSelector, thisDropdown).attr("aria-expanded", "false").parent().removeClass("open").find(dropdown).attr("aria-hidden", "true").slideUp(300, "swing");
                            }
                        }, 500);
                    }
                } else {
                	setTimeout(function () {
                        if(!$(thisDropdown).find(":focus").length) {
                            $(dropdownSelector, thisDropdown).attr("aria-expanded", "false").parent().removeClass("open").find(dropdown).attr("aria-hidden", "true").slideUp(300, "swing");
                        }
                    }, 500);
                }



            });
        },

        "Header": function() {
            // ADD LOGO
            var logoSrc = jQuery.trim("/cms/lib/IL49000024/Centricity/Template/GlobalAssets/images///Logos/RL_Logo.png");
            var srcSplit = logoSrc.split("/");
            var srcSplitLen = srcSplit.length;
            if((logoSrc != "") && (srcSplit[srcSplitLen - 1] != "default-man.jpg")) {
                $(".gb-logo").append("").removeClass("hidden");
            } else {
                $(".gb-logo").append("").removeClass("hidden");
            }

            //SUBPAGE SECTION HEADERS
            if($.trim($(".ui-widget.app.pagenavigation .ui-widget-header").text()) == "") {
                $(".sp-header-column.one").html("
COVID-19");
                if($(".sp").length){
                	if($(".ui-widget.app.calendar").length && "COVID-19" == "") {
                		$(".sp-header-column.one").html("
Calendar");
                	}
                    if($("#swsignin-txt-username").length){
                    	$(".sp-header-column.one").html("
Sign In");
                    }
                    if($("#swageprompt-txt-birthday").length){
                    	$(".sp-header-column.one").html("
Register");
                    }
                }

            } else {
                 $(".sp-header-column.one").html($(".ui-widget.app.pagenavigation .ui-widget-header").html());
            }

        },

        "ChannelBar": function() {
            $(".sw-channel-item").unbind('hover');
            $(".sw-channel-item").hover(function(){
                $(".sw-channel-item > ul").stop(true, true);
                var subList = $(this).children('> ul');
                if ($.trim(subList.html()) !== "") {
                    subList.slideDown(300, "swing");
                }
                $(this).addClass("hover");
            }, function(){
                $(".sw-channel-dropdown").slideUp(300, "swing");
                $(this).removeClass("hover");
            });

            $(".sw-channel-dropdown").each(function() {
                $(this).children().wrapAll('
'); 
            });
        },

        "StickyChannelBar": function() {
            var headerHeight = $("#gb-header").outerHeight();
            var navHeight = $("#gb-channel-list-outer").outerHeight();
            var lastScrollTop = 0;

            $(document).scroll(function() {     
                distanceFromTop = $(this).scrollTop();

                if(distanceFromTop >= headerHeight){
                    $("#gb-page").addClass("sticky-header");
                    $("#gb-header").css("margin-top", navHeight);
                } else {
                    $("#gb-page").removeClass("sticky-header");
                    $("#gb-header").css("margin-top", "0px");
                }

                if(distanceFromTop >= (headerHeight + navHeight)){
                        $("#gb-page").addClass("header-gone");
                    } else {
                        $("#gb-page").removeClass("header-gone");
                    }

                if(distanceFromTop < lastScrollTop){
                    $("#gb-page").removeClass("idle").addClass("show-mobile-menu"); 
                } 
                lastScrollTop = distanceFromTop;

                if($(".sp").length && $(".ui-widget.app.calendar").length) {
                    if($(".wcm-controls").hasClass("wcm-stuck")) {
                         $(".wcm-controls").css("margin-top", navHeight);
                    }
                }

                $("#sw-maincontent").css({
                    "display": "block",
                    "position": "relative",
                    "top": "-" + navHeight + "px"
                });
            });
        },

        "Body": function() {
            // FOR SCOPE
            var _this = this;

            //SHOW MENU ON MOBILE WHEN USER BEGINS TO SCROLL UP
            var scrollTimer;

            scrollTimer = setTimeout(noScroll, 3000); // Initial start of timer
            $(window).bind('scroll', function(event) { // When scrolled
                  clearTimeout(scrollTimer); // Stop the existing timer
                  scrollTimer = setTimeout(noScroll, 3000); // And restart
            });

            function noScroll() {
                  $('#gb-page').addClass("idle").removeClass("show-mobile-menu");
            }

            // AUTO FOCUS SIGN IN FIELD
            $("#swsignin-txt-username").focus();

            // APPLY RESPONSIVE DIMENSIONS TO CONTENT IMAGES
            $(".ui-widget.app .ui-widget-detail img")
                .not($(".ui-widget.app.multimedia-gallery .ui-widget-detail img"))
                .each(function() {
                    if ($(this).attr('width') !== undefined && $(this).attr('height') !== undefined) { // IMAGE HAS INLINE DIMENSIONS
                        $(this).css({"display": "inline-block", "width": "100%", "max-width": $(this).attr("width") + "px", "height": "auto", "max-height": $(this).attr("height") + "px"});
                    }
            });

            // ADJUST FIRST BREADCRUMB
            $("li.ui-breadcrumb-first > a > span").text("Home").show();

            //BIND SP POPULAR LINKS TO DD ACTIONS
            this.DropdownActions({
                "dropdownParent": ".cs-mystart-dropdown.popular-links",
                "dropdownSelector": ".cs-selector",
                "dropdown": ".cs-dropdown",
                "dropdownList": ".cs-dropdown-list"
            });

        },

        "Homepage": function() {
            // FOR SCOPE
            var _this = this;

            //CHECK HP ROWS FOR APPS
            $(".hp-row").each(function(){
            	if($(this).find(".app").length > 0){
                	$(this).removeClass("empty");
                }
            })

            //WRAPPERS FOR HIDING OVERFLOW ON ROW 1 APP HEADERS
            $(".hp-row.one .app").each(function(){
            	$(this).find(".ui-widget-header h1").wrap("
");
            })

            $.fn.isInViewport = function() {
                var elementTop = $(this).offset().top;
                var elementBottom = elementTop + $(this).outerHeight();
                var viewportTop = $(window).scrollTop();
                var viewportBottom = viewportTop + ($(window).height() - 40);
                return elementBottom > viewportTop && elementTop < viewportBottom;
            };

            $(".hp-row.one .app .ui-widget-detail, .hp-row.two .app").addClass("plx");

            $(window).on('resize scroll load', function() {
                $('.hp-row.one .app, .hp-row.one .ui-widget-detail, .hp-row.two .app, .hp-row.four .gb-section, .footer-row.one, .footer-row.two .gb-section, #sp-bottom-header').each(function() {
                    if ($(this).isInViewport()) {
                        $(this).addClass("nowucme");    
                    }
                });
            });

            //SCROLL DOWN
            $("#scroll-down").on('click', function(event){
                //DONT LET THE PAGE JUMP ON KEYPRESS
                event.preventDefault();
                    $('html, body').animate({
                        scrollTop: $(this).offset().top
                }, 500);
            });

            //BIND LIKE TO DD ACTIONS
            this.DropdownActions({
                "dropdownParent": ".cs-mystart-dropdown.like-to",
                "dropdownSelector": ".cs-selector",
                "dropdown": ".cs-dropdown",
                "dropdownList": ".cs-dropdown-list"
            });
        },

        "Footer": function() {
            // FOR SCOPE
            var _this = this;

            // MOVE Bb FOOTER STUFF
            $(".gb-bb-footer.logo").html($("#sw-footer-logo").html());
            var schoolwiresLinks = '';
            schoolwiresLinks += '
' + $.trim($("#sw-footer-links li:eq(0)").html().replace("|", "")) + '';
            schoolwiresLinks += '
' + $.trim($("#sw-footer-links li:eq(2)").html().replace("|", "")) + '';
            schoolwiresLinks += '
' + $.trim($("#sw-footer-links li:eq(1)").html().replace("|", "")) + '';
            $(".gb-bb-footer.links").append('' + schoolwiresLinks + '');
            $(".gb-bb-footer.copyright").append($("#sw-footer-copyright").html());

            //SUBPAGE DEFAULT BACKGROUND IMAGE
            var spFooterBg = jQuery.trim("/cms/lib/IL49000024/Centricity/Template/GlobalAssets/images///Faces/default-man.jpg");
            var srcSplit = spFooterBg.split("/");
            var srcSplitLen = srcSplit.length;
            if((spFooterBg == "") || (srcSplit[srcSplitLen - 1] == "default-man.jpg")) {
                $("#gb-page.sp .footer-row.two").css("background-image","url(/cms/lib/IL49000024/Centricity/Template/17/default-sp-footer-bg.jpg)");
            }

            //VIEW MAP
            $("#show-map").on('click keypress', function(event){
                var mapControl = this;
                if(_this.AllyClick(event) === true){

                    //DONT LET THE PAGE JUMP ON KEYPRESS
                    event.preventDefault();
                    if(!$("body").hasClass("map-open")){
                        $("body").addClass("map-open");
                        $(mapControl).attr("aria-expanded","true");
                        $("#footer-map").animate({
                            height: 342
                        }, 1000).attr("aria-hidden","false");
                        $("#close-map").focus();
                    }
                }
            })

            $("#close-map").on('click keypress', function(event){
                if(_this.AllyClick(event) === true){

                    //DONT LET THE PAGE JUMP ON KEYPRESS
                    event.preventDefault();
                    if($("body").hasClass("map-open")){
                        $("body").removeClass("map-open");
                        $("#show-map").attr("aria-expanded","false");
                        $("#footer-map").animate({
                            height: 0
                        }, 1000).attr("aria-hidden","true");
                        $("#show-map").focus();

                    }
                }
            })

            //REMOVE UNUSED SOCIAL ICONS
            var socialIconToggles = [true, true, true, true, true, false, false, false, false, false];

            for(i = 0; i < socialIconToggles.length; i++){
                if(socialIconToggles[i] == true){
                    $(".footer-row.one").show();
                }
            }

            $(".social-icon[data-toggle='false'], .footer-link[data-toggle='false']").remove();

        },

        "StreamingVideo": function() {
            // FOR SCOPE
            var _this = this;

                var videoVendor = ("YouTube" == "YouTube;Vimeo") ? "youtube" : "YouTube";
                var videoVendor = "YouTube";
                var mobilePhoto = "/cms/lib/IL49000024/Centricity/Template/GlobalAssets/images///BG Photos/69695689_1272931689556389_1941879938588082176_n.jpg";

                if($.trim(mobilePhoto) == "" || mobilePhoto.indexOf("default-man.jpg") > -1) {
                    mobilePhoto = "/cms/lib/IL49000024/Centricity/Template/17/default-mobile.jpg";
                }

                //true

                $("#hp-video #video").fullScreenRotator({
                    "videoSource" : videoVendor, // OPTIONS ARE: youtube, vimeo
                    "videoID": "NuuvHfA73e4", // YouTube and Vimeo ids are set as default in the script
                    "fullScreenBreakpoints" : [960, 768, 640, 480, 320], // OPTIONS ARE [960, 768, 640, 480, 320]
                    "showControls" : true,
                    "showWatchVideoButton" : true,
                    "watchVideoButtonText" : "Watch Background Video",
                    "showProgressBar" : false, // CURRENTLY NOT IN USE
                    "progressBarType" : "line", // CURRENTLY NOT IN USE BUT OPTIONS ARE: line, circle
                    "progressBarCircleWidth": 70, // pixels
                    "progressBarCircleHeight": 70, // pixels
                    "progressBarStrokeWidth": 10, // pixels
                    "progressBarStrokeColor": "#FFF",
                    "showMobileBackgroundPhoto" : true,
                    "mobileBackgroundPhoto" : mobilePhoto,
                    "mobileBackgroundPhotoBreakpoint" : 480, // OPTIONS ARE 768, 640, 480, 320
                    "onReady" : function() { 

                        //WRAP CONTROLS
                        $(".cs-fullscreen-video-button").wrapAll("
");
                        $(".cs-fullscreen-video-button.watch-video-button").insertAfter(".cs-fullscreen-video-button.pause-button");
                        $(".cs-fullscreen-video-buttons").show();

                        //SCROLL DOWN
                        $("#scroll-down").on('click keypress', function(e){
                                if(_this.AllyClick(e) === true){
                                //DONT LET THE PAGE JUMP ON KEYPRESS
                                event.preventDefault();
                                    $('html, body').animate({
                                        scrollTop: $(this).offset().top + 150
                                }, 500);
                            }
                        });

                        //VIDEO BUTTON INTERACTION
                        $("#video .cs-fullscreen-video-button.play-button").attr("aria-label", "Play Background Video");
                        $("#video .cs-fullscreen-video-button.pause-button").attr("aria-label", "Pause Background Video");
                        $('#video .cs-fullscreen-video-button.watch-video-button').attr("aria-label", "Watch Background Video").attr('id', 'streaming-video-popout');

                        $("#video").on("click keydown", ".cs-fullscreen-video-button.play-button", function(e) {
                            if(_this.AllyClick(e)) {
                                setTimeout(function () {
                                    $("#video .cs-fullscreen-video-button.pause-button").focus();
                                }, 100);
                            }
                        });
                        $("#video").on("click keydown", ".cs-fullscreen-video-button.pause-button", function(e) {
                            if(_this.AllyClick(e)) {
                                setTimeout(function () {
                                    $("#video .cs-fullscreen-video-button.play-button").focus();
                                }, 100);
                            }
                        });

                        $('#streaming-video-popout').on('click keydown', function(e){
                            if(e.type == "click" || e.keyCode == 13 || e.keyCode == 32){
                                $('body').addClass('video-popup-showing');
                            }
                        });

                        $('body').on('click keydown', '.cs-fullscreen-video-close', function(e){
                            if(e.type == "click" || e.keyCode == 13 || e.keyCode == 32){
	                            $('body').removeClass('video-popup-showing');
							}
                        });
                    },
                    "onStateChange" : function() { }
                });
        },

        "ModEvents": function() {
            // FOR SCOPE
            var template = this;

            $(".ui-widget.app.upcomingevents").modEvents({
                columns     : "yes",
                monthLong   : "no",
                dayWeek     : "yes"
            });

            eventsByDay(".upcomingevents .ui-articles");

            function eventsByDay(container) {
                $(".ui-article", container).each(function(){
                    if (!$(this).find("h1.ui-article-title.sw-calendar-block-date").size()){
                        var moveArticle = $(this).html();
                        $(this).parent().prev().children().children().next().append(moveArticle);
                        $(this).parent().remove();
                    };
                }); 

                $(".ui-article", container).each(function(){
                    var newDateTime = $('.upcoming-column.left h1', this).html().toLowerCase().split("");
                    var newDate = '';
                    var dateIndex = ((newDateTime.length > 2) ? 2 : 1); //if the dayWeek is set to yes we need to account for that in our array indexing

                    //add a zero to the date if it is a single digit
					if( newDateTime[dateIndex].length == 1 ) newDateTime[dateIndex] = "0" + newDateTime[dateIndex];

                    //if we have the day of the week make sure to include it.
                    if( dateIndex > 1 ){ 
                        newDate += newDateTime[0] + "";
                    }

                    //add in the month
                    newDate += newDateTime[dateIndex - 1] + ''; //the month is always the in the left array position to the date

                    //wrap the date in a new tag
                    newDate += '' + newDateTime[dateIndex] + '';

                    //append the date and month back into their columns
                    $('.upcoming-column.left h1', this).html(newDate);  

                    //add an ALL DAY label if no time was given
                    $('.upcoming-column.right .ui-article-description', this).each(function(){
                        if( $('.sw-calendar-block-time', this).length < 1 ){ //if it doesnt exist add it
                            $(this).prepend('ALL DAY');
                        }
                        $(".sw-calendar-block-time", this).appendTo($(this));
                    });

                    //WRAP DATE AND MONTH IN A CONTAINER
                    $(".jeremy-date, .joel-month", this).wrapAll("");
                    $(".joel-month", this).wrapInner("");
                }) 
            }

            //ADD NO EVENTS TEXT
            $(".upcomingevents").each(function(){
                if(!$(this).find(".ui-article").length){
                    $("

There are no upcoming events to display.").appendTo($(this).find(".ui-articles"));
                }
            });

        },

