        "Search": function() {
            // FOR SCOPE
            var _this = this;

            //OPENING SEARCH
            $("#gb-search-input").on('focus', function(){
                $("#gb-search").addClass("open");
            })

            $("#gb-search-input").on('focusout', function(){
                if($("#gb-search-input").val() == "") {
                    $("#gb-search").removeClass("open");
                } 
            })

            //HOVER SEARCH
            $("#gb-search").hover(function(){
                $(this).addClass("open");
            }, function(){
                if($("#gb-search-input").val() == "Search"){
                	$(this).removeClass("open");
                }
            });

            //SEARCH FORM SUBMISSION
            $("#gb-search-form").submit(function(e){
                e.preventDefault();

                if($("#gb-search-input").val() != "Search" || $("#gb-search-input").val() != "") {
                    window.location.href = "/site/Default.aspx?PageType=6&SiteID=4&SearchString=" + $("#gb-search-input").val();
                }
            });

            //SEARCH INPUT TEXT
            $("#gb-search-input").focus(function() {
                if($(this).val() == "Search") {
                    $(this).val("");
                }
            });

            $("#gb-search-input").blur(function() {
                if($(this).val() == "") {
                    $(this).val("Search");
                }
            });

            if($(".sp").length || $(".spn").length) {
            	$(document).on("click", "#gb-search-toggle", function(e) {
                    if(_this.AllyClick(e)) {
                        e.preventDefault();

                        if($(this).attr("aria-expanded") == "false") {
                            $(this).attr("aria-expanded", "true");

                            $("#gb-header").addClass("search-open");
                            $("#gb-search-outer").attr("aria-hidden", "false").slideDown();

                            $("#gb-search-input").attr({
                                "aria-hidden": "false",
                                "tabindex": "0"
                            });

                            $("#gb-search-button").attr({
                                "aria-hidden": "false",
                                "tabindex": "0"
                            });

                            setTimeout(function() {
                            	$("#gb-search").addClass("open");
                            },300);

                        } else {
                            $(this).attr("aria-expanded", "false");

							$("#gb-search").removeClass("open");
                            $("#gb-header").removeClass("search-open");
                            $("#gb-search-outer").attr("aria-hidden", "true").slideUp();

                            $("#gb-search-input").attr({
                                "aria-hidden": "true",
                                "tabindex": "-1"
                            });

                            $("#gb-search-button").attr({
                                "aria-hidden": "true",
                                "tabindex": "-1"
                            });
                        }
                    }
            	});

                $("#gb-search-outer #gb-search").hover(function(){
                    $(this).addClass("open");
                }, function(){
                    if($("#gb-search-input").val() == "Search"){
                        $(this).addClass("open");
                    }
                });

                $(document).on("keydown", "#gb-search-toggle", function(e) {
                    if(_this.AllyClick(e)) {
                        e.preventDefault();

                        if($(this).attr("aria-expanded") == "false") {
                            $(this).attr("aria-expanded", "true");

                            $("#gb-header").addClass("search-open");
                            $("#gb-search-outer").attr("aria-hidden", "false").slideDown();

                            $("#gb-search-input").attr({
                                "aria-hidden": "false",
                                "tabindex": "0"
                            }).focus();

                            $("#gb-search-button").attr({
                                "aria-hidden": "false",
                                "tabindex": "0"
                            });

                        } else {
                            $(this).attr("aria-expanded", "false");

                            $("#gb-header").removeClass("search-open");
                            $("#gb-search-outer").attr("aria-hidden", "true").slideUp();

                            $("#gb-search-input").attr({
                                "aria-hidden": "true",
                                "tabindex": "-1"
                            });

                            $("#gb-search-button").attr({
                                "aria-hidden": "true",
                                "tabindex": "-1"
                            });

                        }
                    }
            	});

                $(document).on("keyup", function() {
                    if($("#gb-search-outer").attr("aria-hidden") == "false" && !$("#gb-search-form").find(":focus").length && !$("#gb-search-toggle").is(":focus")) {
                            $("#gb-search-toggle").attr("aria-expanded", "false").focus();

                            $("#gb-header").removeClass("search-open");
                            $("#gb-search-outer").attr("aria-hidden", "true").slideUp();

                            $("#gb-search-input").attr({
                                "aria-hidden": "true",
                                "tabindex": "-1"
                            });

                            $("#gb-search-button").attr({
                                "aria-hidden": "true",
                                "tabindex": "-1"
                            });
                        }
                    });

                    $("#gb-search-input").focusout(function() {
                        setTimeout(function() {
                            if(!$("#gb-search-input").find(":focus").length && !$("#gb-search-toggle").is(":focus") && !$("#gb-search-button").is(":focus")) {
                                $("#gb-search-toggle").attr("aria-expanded", "false");

                            $("#gb-header").removeClass("search-open");
                            $("#gb-search-outer").attr("aria-hidden", "true").slideUp();

                            $("#gb-search-input").attr({
                                "aria-hidden": "true",
                                "tabindex": "-1"
                            });

                            $("#gb-search-button").attr({
                                "aria-hidden": "true",
                                "tabindex": "-1"
                            });
                            }
                        }, 500);
                    }).keydown(function(e) {
                        if(e.keyCode == _this.KeyCodes.esc) {
                            e.preventDefault();

                            $("#gb-search-toggle").attr("aria-expanded", "false").focus();

                            $("#gb-header").removeClass("search-open");
                            $("#gb-search-outer").attr("aria-hidden", "true").slideUp();

                            $("#gb-search-input").attr({
                                "aria-hidden": "true",
                                "tabindex": "-1"
                            });

                            $("#gb-search-button").attr({
                                "aria-hidden": "true",
                                "tabindex": "-1"
                            });
                        }
                    });
            }

            if (window.location.href.indexOf("&SearchString=") > -1) {
                $(".sp-header-column.one h1").text("Search");
            }
        },

        "AllyClick": function(event) {
            if(event.type == "click") {
                return true;
            } else if(event.type == "keydown") {
                if(event.keyCode == this.KeyCodes.space || event.keyCode == this.KeyCodes.enter) {
                    return true;
                }
            } else {
                return false;
            }
        },

        "GetBreakPoint": function() {
            return window.getComputedStyle(document.querySelector("body"), ":before").getPropertyValue("content").replace(/"|'/g, "");/*"*/
        }
    };

