function onPageLoad() {
    var statusstateshare = 0;
    var wwidth = $(window).width();
    var wheight = $(window).height();
    $('[href*="donate"]').addClass('no-barba');

    $('h1').html(function(i,html){
        return html.replace(/^\s*([^\s]+)(\s|$)/, '$1 ');
    });
    $('h2').html(function(i,html){
        return html.replace(/^\s*([^\s]+)(\s|$)/, '$1 ');
    });

    (function() {
        var youtube = document.querySelectorAll(".youtube");
        for (var i = 0; i < youtube.length; i++) {
            var source = youtube[i].dataset.preview;
            var alt = youtube[i].dataset.alt;
            var image = new Image();
            image.src = source;
            image.alt = alt;
            image.addEventListener("load", function() {youtube[i].appendChild(image);}(i));
            youtube[i].addEventListener("click", function() {
                var iframe = document.createElement("iframe");
                iframe.setAttribute("frameborder", "0");
                iframe.setAttribute("allowfullscreen", "");
                iframe.setAttribute("src", "https://www.youtube.com/embed/" + this.dataset.embed + "?rel=0&showinfo=0&autoplay=1");
                this.innerHTML = "";
                this.appendChild(iframe);
            });
        };
    })();

    $('.accordion').find('.accordion-section-title').click(function() {
        //Expand or collapse this panel
        $(this).next().slideToggle('fast');
        //Hide the other panels
        $(".accordion-section-content").not($(this).next()).slideUp('fast');
    });

    $(".wym img").addClass("img-responsive");
    $(".navbar-nav ul").addClass("dropdown-menu");
	$(".navbar-nav ul").parent().addClass("dropdown");
	$(".navbar-nav ul").prev().addClass("dropdown-toggle");
    $("iframe").css({
        'max-width': (100 + '%'),
    });
    $('#share_icon').click(function() {$('#share_area').slideToggle(350);});

    fred();
    if ($(window).width() > 767) {equalHeight('.testform');}

    $(this).delay(200).queue(function() {
        onResize();
        fred();
        $(this).dequeue();
    });
	if($("#alert_message").length != 0) {
		//$('body').css('padding-top', $("#alert_message").outerHeight());
		if(Cookies.get('alert') != 'closed') {
			$('.header').css('top', $("#alert_message").outerHeight());
		}
		$('#alert_close').click(function() {
			Cookies.set('alert', 'closed');
			$('#alert_message').css('display', 'none');
			$('.header').css('top', '0px');
		})
	}
	if(Cookies.get('alert') == 'closed') {
		$('#alert_message').css('display', 'none');
	}
	if(Cookies.get('alert') != 'closed') {
		if($(window).width() < 768) {
			$('header').css('top', 'auto');
			$('.overlay_header').css('top', $('#alert_message').outerHeight(true));
		}
		$('#alert_close').click(function() {
			Cookies.set('alert', 'closed');

			$('.alert_active').css('padding-top', '0px');
			$('#header_inner_btns').css('top', '0px');
			$('header').css('top', '0px');
		})
	}
}

