function mobileNav(group) {
    $(group).each(function () {
        if ($(this).hasClass("has-children")) {
            parentlink = $(this).children().first("a").clone(true);
            parentlinkwrap = '
' + parentlink + '';
            $(this).find('ul').prepend(parentlinkwrap);
            $(this).find('ul').children('li').first().html(parentlink);
        }
    });
}

function equalHeight(group) {
    tallest = 0;
    $(group).each(function() {
        thisHeight = $(this).height();
        if (thisHeight > tallest) {
            tallest = thisHeight;
        }
    });
    $(group).height(tallest);
}
function msheight(group) {
    tallestMS = 0;
    $(group).each(function() {
        thisHeightMS = $(this).outerHeight(true);
        if (thisHeightMS > tallestMS) {
            tallestMS = thisHeightMS;
        }
    });
    tallestMSFinal = tallestMS;
    $(group).height(tallestMSFinal);
}
function comin(group) {
    tallestMS = 0;
    $(group).each(function() {
        thisHeightMS = $('.cotextwrap').height();
        thisHeightMS = thisHeightMS + $('.coimg').height();
        if (thisHeightMS > tallestMS) {
            tallestMS = thisHeightMS;
        }
    });
    tallestMSFinal = tallestMS;
    $(group).css({
            'min-height': tallestMSFinal,
        });
}
