    var CreativeTemplate = {
    	// PROPERTIES
        "KeyCodes": { "tab": 9, "enter": 13, "esc": 27, "space": 32, "end": 35, "home": 36, "left": 37, "up": 38, "right": 39, "down": 40 },
        "IsMyViewPage": false, // UPDATES IN SetTemplateProps METHOD
        "IconList": "",

        // METHODS
        "Init": function() {
            // FOR SCOPE
            var _this = this;

            this.SetTemplateProps();
            this.GlobalIcons(_this);
            this.RsMenu(_this);
            this.MyStart();
            this.Header();
            this.ChannelBar();
            this.Dropdown();
            this.Body();
            this.Footer();
            this.Slideshow();
            this.Search();
            this.WindowScroll();
            this.WindowResize();
            this.AppAccordion();

            $(document).ready(function(){

                if($('#ui-btn-mypasskey').length) {
                    $('
').insertAfter($('.cs-footer-link.district'));
                    $('.cs-footer-link.passkeys').append($('#ui-btn-mypasskey'));
                    $('.cs-footer-link.passkeys a').addClass('cs-fc cs-aic').text('Passkeys').attr('position', 'bottomright');
                } 

            });
            $(window).load(function(){ _this.WindowLoad();  });
            $(window).resize(function(){ _this.WindowResize(); });
            $(window).scroll(function(){_this.WindowScroll();});

            // HANDLE LINKS THAT OPEN IN A NEW WINDOW
			csGlobalJs.OpenInNewWindowWarning();

            $("#samlsignin").insertBefore("#swlogin"); /* added by Matt B - case#4055400 - 01.02.19 */
        },

        "SetTemplateProps": function() {
        	// MYVIEW PAGE CHECK
            if($("#pw-body").length) this.IsMyViewPage = true;
        },

        "WindowLoad": function() {
            this.CalendarListView();
			$('#sw-mystart-outer').remove();

            // MAKE NAVIGATION APPS ACCESSIBLE
            $(".ui-widget.app.navigation").csAccessibleNavigationApp();

            //-->> Add Bullet Collapse
            $('.ui-widget.navigation .bullet').each(function(k, v){
            	if($(v).hasClass('expandable') || $(v).hasClass('collapsible')) {
                	$(v).append('');
                }
            });

            // HANDLE LINKS THAT OPEN IN A NEW WINDOW
			csGlobalJs.OpenInNewWindowWarning();

            //DISABLE SEARCH IF INPUT IS EMPTY OR DEFAULT
            $("#sw-search-input").unbind();
            $("#gb-search-wrapper").on("keydown", "#sw-search-input", function(e) {
                if(e.keyCode === 13) {
                    e.preventDefault();

                    if($.trim($("#sw-search-input").val()) != "Search This Site" && $.trim($("#sw-search-input").val()) != "") {
                        SWGoToSearchResultsPageswsearchinput();
                    }
                }

                if(e.shiftKey && e.keyCode === 9) {
                    e.preventDefault();

                    $("#gb-search-btn").focus();
                }
            });

        },

        "WindowResize": function() {

            this.CalendarListView();
            var wW = $(window).width();

            if(wW < 1024) {

                $('#hp-slideshow-outer .gb-section').addClass('cs-fw');
            	$('#hp-tabbed-apps .site-shortcuts').addClass('cs-fc cs-fw');
            	if($('.hp.column .mobile-svg').size() == 0) {
                	$('.hp.column .ui-widget-header').append('');
            	}
                $('.gb-address-wrapper, #gb-office, #gb-class').addClass('cs-jc').removeClass('cs-jfs');

                if(!$('.ui-widget.app.gallery').hasClass('open')) {
                	$('.ui-widget.app.gallery .ui-widget-header').click();
                }
                if(wW < 640 && !$('.sp.column.one .mobile-svg').length) {
                	$('.sp.column.one .ui-widget-header').append('');
                }
                $('#gb-schoolwires-footer').removeClass('cs-fw');
                if(wW < 480) {
                	$('#gb-schoolwires-footer').addClass('cs-fw');
                }
            } else {

                $('#hp-slideshow-outer .gb-section').removeClass('cs-fw');
            	$('#hp-tabbed-apps .site-shortcuts').removeClass('cs-fc cs-fw');
            	$('.hp.column .ui-widget-header .mobile-svg').remove();
                $('.gb-address-wrapper, #gb-office, #gb-class').removeClass('cs-jc').addClass('cs-jfs');

            }

        },

        "WindowScroll": function() {
        	var wScroll = $(window).scrollTop(),
            	nOffset = $('header').height(),
                nHeight = $('nav').height();

            //--> Close Search if Scrolling.
            if(wScroll > 20 && $('#gb-page').hasClass('search-open')) {
            	$('#gb-search-btn').click();
            }

            //--> Sticky Channel Bar
            if(wScroll > nOffset) {
            	$('nav').addClass('fixed');
            	$('#gb-header-outer').css('margin-bottom', nHeight + 'px');
            } else {
            	$('nav').removeClass('fixed');
            	$('#gb-header-outer').css('margin-bottom', '0px');
            }
        },

        "MyStart": function() {
        	// ADD TRANSLATE
            /*
        	$("#gb-header-right #gb-translate-dropdown").creativeTranslate({
                "type": 1,
			"languages": [ // ["ENGLISH LANGUAGE NAME", "TRANSLATED LANGUAGE NAME", "LANGUAGE CODE"]
                  ["Spanish", "Español", "es"],
