            $('#gb-search-btn').unbind('click').click(function(e){

            	e.preventDefault();

            	if($(this).hasClass('open')) {

                	$('#gb-page').removeClass('search-open');
                    $('#sw-search-input').attr('aria-hidden', 'true');
                    $('#gb-search-outer #gb-search-wrapper').slideUp().attr('aria-hidden', 'true');
                	$(this).removeClass('open').attr('aria-label', 'Open Search');

                } else {

                	$('#gb-page').addClass('search-open');
            		$('#gb-search-outer #sw-search-input').attr('aria-hidden', 'false').attr('tabindex', '0').addClass('open');
                    $('#sw-search-input').attr('aria-hidden', 'false');
                    $('#gb-search-outer #gb-search-wrapper').slideDown(function(){
                    	$('#sw-search-input').focus();
                    }).attr('aria-hidden', 'false');
                	$(this).addClass('open').attr('aria-label', 'Close Search');

                }

            });

