		/****** REMOVE First 0S *******/
		$.each($('.jeremy-date'), function(){
		        var text = $(this).text(),
		            html = '';
		        if(text.slice(0, 1) == 0){
		            html += text.slice(1);
		        } else {
		            html += text.slice(0);
		        }
		        $(this).text(html);
		});// End each
	}

        },

