        "Search": function() {
            //SEARCH TEXT
            $("#sw-search-input").focus(function() {
                if($(this).val() == "Search This Site") {
                    $(this).val("");
                }
            }).blur(function() {
                if($(this).val() == "") {
                    $(this).val("Search This Site");
                }
            });
