                }
            });*/
            $('.app.headlines').each(function(k, v){
                if($(v).closest('#hp-row-four').size() != 1 && $('a.more-link svg', v).size() != 1){
                    $('.ui-widget-header', v).append($('a.more-link', v));
                    $('a.more-link', v).attr('title', 'View all.').html(svgs.plus + 'VIEW ALL');
