            	$('.show-toggle', this).fadeOut().attr('aria-hidden', 'true');
            	$('.plus-icon', this).fadeIn();
            }).focus(function(){
            	$('.show-toggle', this).fadeIn().attr('aria-hidden', 'false');
            	$('.plus-icon', this).fadeOut();
            }).blur(function(){
            	$('.show-toggle', this).fadeOut().attr('aria-hidden', 'true');
            	$('.plus-icon', this).fadeIn();
            });

            /*
            $('.app.headlines a.more-link').hover(function(){
