        function onYouTubeIframeAPIReady() {

            // CREATE THE IFRAME AFTER API IS LOADED
            var player;
            player = new YT.Player("youtube-video", {
                height: $(window).height(),
                width: $(window).width(),
                videoId: "On5mx4Ltda8",
                playerVars: { 
                    "modestbranding": 1,
                    "controls": 0, 
                    "rel" : 0,
                    "loop": 1,
                    "autoplay": 1,
                    "wmode": "opaque"
                },
                events: {
                    // YOUTUBE PLAYER IS READY
                    "onReady": function() {
                        // SET VIDEO DIMENSIONS
                        youTubeVideoDims();

                        // MUTE THE VIDEO
                        player.mute();

                        // MAKE SURE TO AUTO PLAY
                        player.playVideo();

                        // BIND VIDEO EVENTS
                        $('#video-btn').click(function(){
                        	//PLAY
                            if($(this).hasClass('paused')){
                            	player.playVideo();
                                $(this).removeClass("paused").attr('aria-label', 'Pause video.');
                                $('.text', this).typed({
                                    strings: ["PAUSE"],
                                    startDelay: 100,
                                    typeSpeed: 30
                                });
                            } 
                            //PAUSE
                            else {
                                player.pauseVideo();
                                $(this).addClass("paused").attr('aria-label', 'Play video.');
                                $('.text', this).typed({
                                    strings: ["PLAY"],
                                    startDelay: 100,
                                    typeSpeed: 30
                                });
                            }
                        });
                    },
                    "onStateChange": function(){
                        // VIDEO HAS ENDED
                        if(player.getPlayerState() == 0) {
                            player.playVideo();
                        }
                    },
                    "onError": function(e){
                    	console.log(e);
                    }
                }
            });
            $('#youtube-video iframe').attr('tabindex', '-1');
        }


        function youTubeVideoDims() {
            // GET SCREEN DIMENSIONS
            var screenWidth = $(window).width();
            var screenHeight = $(window).height();
            var vidWidth = screenWidth, vidHeight = 0;

            // SET WIDTH, HEIGHT, AND RATIO
            var initW = 853.5, initH = 480;
            var ratio = initH / initW;
            vidHeight = vidWidth * ratio;
            if(vidHeight < screenHeight){
                vidHeight = screenHeight;
                vidWidth = vidHeight / ratio;
            }

            // CENTER VIDEO
            var centerWidth = (vidWidth - screenWidth) / 2;
            var centerHeight = (vidHeight - screenHeight) / 2;

            // APPLY THE DIMENSIONS
            $("#youtube-video").css({"width": vidWidth + "px", "height": vidHeight + "px", "margin": "-" + centerHeight + "px 0px 0px -" + centerWidth + "px"});
        }
      }

        function rotateHeadlines() {
        	var vpw = $(window).width();
            if(vpw >= 1024){
                $("#hp-slideshow div.ui-widget.app.headlines").hide();
                $("#hp-slideshow div.ui-widget.app.headlines").eq(0).show();
                $("#hp-slideshow div.ui-widget.app.headlines").eq(0).each(function() {
                    if(!$(".active", this).size()) {
                        var index = 0;
                        $("li", this).eq(index).addClass("active");
                        $(".active", this).animate({ opacity: 1 }, 1500, function() { setTimeout(function () { rotateHeadlines(); }, 8000); });
                    } else {
                        var index = $(".active", this).index();
                        $(".active", this).animate({ opacity: 0 }, 1500, function() { }); 
                        $(".active", this).removeClass("active");
                        if(index == $("li", this).length-1) {
                            $("li", this).eq(0).addClass("active");
                        }
                        else {
                            $("li", this).eq(index).next("li").addClass("active");
                        }
                        $(".active", this).animate({ opacity: 1 }, 1500, function() { setTimeout(function () { rotateHeadlines(); }, 8000); });
                    }
                });
            } else {
                $("#hp-moved-apps div.ui-widget.app.headlines").hide();
                $("#hp-moved-apps div.ui-widget.app.headlines").eq(0).show();
                $("#hp-moved-apps div.ui-widget.app.headlines").eq(0).each(function() {
                    if(!$(".active", this).size()) {
                        var index = 0;
                        $("li", this).eq(index).addClass("active");
                        $(".active", this).animate({ opacity: 1 }, 1500, function() { setTimeout(function () { rotateHeadlines(); }, 8000); });
                    } else {
                        var index = $(".active", this).index();
                        $(".active", this).animate({ opacity: 0 }, 1500, function() { }); 
                        $(".active", this).removeClass("active");
                        if(index == $("li", this).length-1) {
                            $("li", this).eq(0).addClass("active");
                        }
                        else {
                            $("li", this).eq(index).next("li").addClass("active");
                        }
                        $(".active", this).animate({ opacity: 1 }, 1500, function() { setTimeout(function () { rotateHeadlines(); }, 8000); });
                    }
                });
            }
		}

    //--> Check Images for Defaults
    function defaultImg(loc, img, def, alt){
        var src = jQuery.trim(img);
        var srcSplit = src.split("/");
        var srcSplitLen = srcSplit.length;
        if((src != "") && (srcSplit[srcSplitLen - 1] != "default-man.jpg")) {
            $(loc).append('').removeClass("hidden");
        } else {
            $(loc).append('').removeClass("hidden");
        }
    }

	function appView(){
    	if($(".hp").length) {
          //DOC dont add app into accordion if it doesnt have a header
          $("#hp-content div.ui-widget.app").each(function() {
              if($(".ui-widget-header", this).hasClass("ui-helper-hidden") || jQuery.trim($(".ui-widget-header h1", this).text()) == "") {
                  $(this).addClass("no-header");
              }
          });

          //DOC accordion header click
          $(".mobile-btn").off('click').click(function(){
              	var id = $(this).attr('id'),
              		id = id.replace('-btn', '');
              	if($(this).parent().parent().hasClass('headlines') && !($(this).hasClass('open'))){
                	//--> Run Masonry 
                    var $grid = $('#hp-row-one .headlines .ui-articles, .sp .headlines .ui-articles').imagesLoaded( function() {
                        // init Masonry after all images have loaded
                        $grid.masonry({
                            itemSelector: '#hp-row-one .headlines .ui-articles > li, .sp .headlines .ui-articles > li',
                            columnWidth: 266
                        });
                    });
                }
                if($(this).hasClass('open')){
                	$(this).removeClass('open');
                    $(this).parent().removeClass('open');
                  	$('#' + id + '-detail').removeClass('open').slideUp();
              	} else {
                  	$(this).addClass('open');
                    $(this).parent().addClass('open');
                  	$('#' + id + '-detail').addClass('open').slideDown();
              	}
          });
        }
	}	

