        //--> Search Button
        $('#cs-custom-search').click(function(){
        	if($(this).hasClass('open')){
            	$(this).removeClass('open').attr('aira-label', 'Open Search');
                $('#gb-search-btn .cs-btn-tag').text('Open Search');
                $('#gb-search-btn #sw-search-input').removeClass('open').attr('aria-hidden', 'true').attr('tabindex', '-1');
            } else {
            	$(this).removeClass('hover').addClass('open').attr('aria-label', 'Close Search');
                $('#gb-search-btn .cs-btn-tag').text('Close Search');
                $('#gb-search-btn #sw-search-input').addClass('open').attr('aria-hidden', 'false').attr('tabindex', '0').delay(500).focus();
            }
        }).keydown(function(e){
        	var c = e.which;
            if(c == csKeyCodes.enter){
            	e.preventDefault();
            }
        }).keyup(function(e){
        	var c = e.which;
            if(c == csKeyCodes.enter || c == csKeyCodes.space){
            	e.preventDefault();
                $(this).click();
            }
        });


        $('#gb-district-btn a').focus(function(){
        	$('#gb-district-btn').addClass('hover');
