		//--> Dropdown Variables
        var dFocus;

        //--> Dropdown Functionality
        $('.cs-custom-dropdown button').click(function(){
        	var id = $(this).attr('aria-controls');
        	if($(this).hasClass('open')){
            	$(this).removeClass('open').attr('aria-expanded', 'false');
                $('#'+id).attr('aria-hidden', 'true').removeClass('open').stop().slideUp();
            } else {
            	$(this).addClass('open').attr('aria-expanded', 'true');
                $('#'+id).attr('aria-hidden', 'false').addClass('open').slideDown();
            }
        }).keydown(function(e){
        	var c = e.which,
            	id = $(this).attr('aria-controls');
            if(c == csKeyCodes.down || c == csKeyCodes.enter || c == csKeyCodes.space){
            	e.preventDefault();
                if((c == csKeyCodes.down && $(this).hasClass('open')) && !($(this).hasClass('inner'))){
                    $('#' + id + ' a').first().delay(700).focus();
                    $(this).addClass('inner');
                } else if((c == csKeyCodes.down && !($(this).hasClass('inner'))) || c == csKeyCodes.enter || c == csKeyCodes.space){
                    if(!($(this).hasClass('open'))){
                		$(this).click().addClass('inner');
                    	$('#' + id + ' a').first().delay(700).focus();
                    } else {
                		$(this).click();
                    }
                }
            }
        }).focus(function(){
        	if($(this).hasClass('inner')){
            	$(this).removeClass('inner');
            }
        }).blur(function(){
        	if($(this).hasClass('open')){
                var d = $(this);
                dFocus = setTimeout(function(){ d.removeClass('focus').click();}, 300);
            }
        }).hover(function(){
            	$(this).click();
        }, function(e){
        	if($(this).hasClass('open')){
                var d = $(this);
                dFocus = setTimeout(function(){ d.removeClass('focus').click();}, 300);
            }
        });

        $('.cs-dropdown').hover(function(){
        	if($(this).hasClass('open')){
                clearTimeout(dFocus);
            }
        }, function(){
        	var btn = $(this).siblings('button');
        	if($(this).hasClass('open')){
                dFocus = setTimeout(function(){ btn.removeClass('focus').click();}, 300);
            }
        });

        $('.cs-custom-dropdown').each(function(m, n){
        	$('.cs-dropdown-list a', n).each(function(k, v){
            	$(v).attr('data-link', k + 1).attr('tabindex', '-1');
            });
        });


        $('.cs-dropdown-list a').keydown(function(e){
        	var c = e.which,
              	link = $(this).data('link');
            if(c == csKeyCodes.down || c == csKeyCodes.up){
            	e.preventDefault();
                if(c == csKeyCodes.down && $('.open a[data-link="' + (link + 1) + '"]').html() == undefined) {
                	$('.open a[data-link="' + 1 + '"]').focus();
                } else if(c == csKeyCodes.up && $('.open a[data-link="' + (link - 1) + '"]').html() == undefined){
                    $(this).parent().parent().parent().children('button').focus();
                } else if(c == csKeyCodes.down){
                	$('.open a[data-link="' + (link + 1) + '"]').focus();
                } else {
                	$('.open a[data-link="' + (link - 1) + '"]').focus();
                }
            }
        }).focus(function(){
        	clearTimeout(dFocus);
        });

        //--> Scrool Down Button
        $('#cs-scroll-dwn-btn').click(function(e){
