body.script:
$(window).scroll(function() {
    if ($(this).scrollTop() > 250){  
        $('.navbar-top').addClass("sticky");
    }
    else{
        $('.navbar-top').removeClass("sticky");
    }
    if ($(this).scrollTop() > 250){  
        $('.main_logo').addClass("sticky");
    }
    else{
        $('.main_logo').removeClass("sticky");
    }
});

function previouspage() {

	history.back();
}
