function googleTranslateElementInit() {
    new google.translate.TranslateElement({ pageLanguage: 'en', layout: google.translate.TranslateElement.InlineLayout.SIMPLE }, 'google_translate_element');
}

	//
if ($('#interior-side-bar-column')[0].children.length === 0 && $('#interior-child-side-bar-column')[0].children.length === 0) {
    $('#interior-main-content-area').removeClass('col-sm-9');
    $('#interior-main-content-area').addClass('col-sm-12');
    $('#interior-side-bar-column').css('display', 'none');
    $('#interior-child-side-bar-column').css('display', 'none');
} else if ($('#interior-side-bar-column')[0].children.length > 0 && $('#interior-child-side-bar-column')[0].children.length === 0) {
    $('#interior-main-content-area').removeClass('col-sm-9');
    $('#interior-main-content-area').addClass('col-sm-12');
    $('#interior-side-bar-column').css('display', 'none');
    $('#interior-child-side-bar-column').css('display', 'none');
} else if ($('#interior-side-bar-column')[0].children.length === 0 && $('#interior-child-side-bar-column')[0].children.length > 0) {
    $('#interior-side-bar-column').css('display', 'none');
} else if ($('#interior-side-bar-column')[0].children.length > 0 && $('#interior-child-side-bar-column')[0].children.length > 0) {
    $('#interior-side-bar-column').css('display', 'none');
}

