$(document).ready(function() {
    $('.homeNewsWrapper').each(function() {
        var i = 1;
        $(this).find(".tab-content").each(function() {
            $(this).attr('data-tabnumber', 'tab-' + i);
 
            var thisTitle = $(this).find('.newsTitle').text();
            $(this).closest('.homeNewsWrapper').find('.tabs-menu').append('
' + thisTitle + '');
 
            i++
 
        });
    
        $(this).find('.tabs-menu li').first().addClass('current');
        $(this).find('.tab .tab-content').first().addClass('current');
    });
    
    $(".tabs-menu a").click(function(event) {
        event.preventDefault();
        $(this).parent().addClass("current");
        $(this).parent().siblings().removeClass("current");
        var tab = $(this).attr("data-tabnumber");
        $(this).closest('.homeNewsWrapper').find(".tab-content").not($('.tab-content[data-tabnumber="' + tab + '"]')).css("z-index", "0").removeClass('current');
        $(this).closest('.homeNewsWrapper').find('.tab-content[data-tabnumber="' + tab + '"]').css("z-index", "1").addClass('current');
        $(this).closest('.homeNewsWrapper').find('.tab-content .image .inside').imagefill();
    });
});

