              new google.maps.LatLng(39.786259, -85.957332),
              new google.maps.LatLng(39.782584, -85.957214),
              new google.maps.LatLng(39.7826, -85.955068),
              new google.maps.LatLng(39.780514, -85.955207),
              new google.maps.LatLng(39.780251, -85.953652)
          ];

          // Construct the polygon
          // Note that we don't specify an array or arrays, but instead just
          // a simple array of LatLngs in the paths property
          polybrtr = new google.maps.Polygon({
              paths: brtrCoords,
              strokeColor: "#7fdfff",
              strokeOpacity: 0.8,
              strokeWeight: 2,
              fillColor: "#7fdfff",
              fillOpacity: 0.45
          });

          //Sunny Heights coords
          var shtrCoords = [
              new google.maps.LatLng(39.806916, -85.952439),
