      function HideAllPolygonsMarkers() {
          // hide all
          polyhatr.setMap(null);
          polyhaxtr.setMap(null);
          markerha.setMap(null);
          polyprtr.setMap(null);
          markerpr.setMap(null);
          polylptr.setMap(null);
