      }

      function addPolygon(strLocation) {
          if (strLocation == 'pr')
              polyprtr.setMap(map);
          else if (strLocation == 'ha')
              polyhatr.setMap(map);
          else if (strLocation == 'hax')
              polyhaxtr.setMap(map);
          else if (strLocation == 'lp')
              polylptr.setMap(map);
          else if (strLocation == 'gc')
              polygctr.setMap(map);
          else if (strLocation == 'gcx')
              polygcxtr.setMap(map);
          else if (strLocation == 'ea')
              polyeatr.setMap(map);
          else if (strLocation == 'la')
              polylatr.setMap(map);
          else if (strLocation == 'wchs')
              polywchs.setMap(map);
          else if (strLocation == 'lo')
              polylotr.setMap(map);
          else if (strLocation == 'lox')
              polyloxtr.setMap(map);
          else if (strLocation == 'br')
              polybrtr.setMap(map);
          else if (strLocation == 'sh')
              polyshtr.setMap(map);
          else if (strLocation == 'cr')
              polycrtr.setMap(map);
          else if (strLocation == 'st')
              polysttr.setMap(map);
          else if (strLocation == 'stx')
              polystxtr.setMap(map);
          else if (strLocation == 'rp')
              polyrptr.setMap(map);
          else if (strLocation == 'rpx')
              polyrpxtr.setMap(map);
          else if (strLocation == 'rpxx')
              polyrpxxtr.setMap(map);
      }

      function addMarker(strLocation) {
          if (strLocation == 'prm')
              markerpr.setMap(map);
          else if (strLocation == 'ham')
              markerha.setMap(map);
          else if (strLocation == 'lpm')
              markerlp.setMap(map);
          else if (strLocation == 'gcm')
              markergc.setMap(map);
          else if (strLocation == 'eam')
              markerea.setMap(map);
          else if (strLocation == 'lam')
              markerla.setMap(map);
          else if (strLocation == 'wchsm')
              markerwchs.setMap(map);
          else if (strLocation == 'lom')
              markerlo.setMap(map);
          else if (strLocation == 'brm')
              markerbr.setMap(map);
          else if (strLocation == 'shm')
              markersh.setMap(map);
          else if (strLocation == 'rpm')
              markerrp.setMap(map);
          else if (strLocation == 'crm')
              markercr.setMap(map);
          else if (strLocation == 'stm')
              markerst.setMap(map);
      }

      function ShowTraditional() {
          HideAllPolygonsMarkers();
          addPolygon('pr');
          addMarker('prm');
          addPolygon('ha');
          addPolygon('hax');
          addMarker('ham');
          addPolygon('lp');
          addMarker('lpm');
          addPolygon('gc');
          addPolygon('gcx');
          addMarker('gcm');
          addPolygon('ea');
          addMarker('eam');
          addPolygon('la');
          addMarker('lam');
          addPolygon('lo');
          addPolygon('lox');
          addMarker('lom');
          addPolygon('br');
          addMarker('brm');
          addPolygon('sh');
          addMarker('shm');
      }

      function ShowHighSchool() {
          HideAllPolygonsMarkers();
          addPolygon('wchs');
          addMarker('wchsm');
      }

      function ShowMiddleTR() {
          HideAllPolygonsMarkers();
          addPolygon('cr');
          addMarker('crm');
          addPolygon('rp');
          addPolygon('rpx');
          addPolygon('rpxx');
          addMarker('rpm');
          addPolygon('st');
          addPolygon('stx');
          addMarker('stm');
      }

      function codeAddress() {
          var address = document.getElementById("address").value;
          var homeimage = "https://az480170.vo.msecnd.net/0975b797-d19b-4bf5-a32f-b341aca3a157/img/orig/2aae38b7-0f03-4f27-9f1e-8bf01c4947b9/home.png";
          if (geocoder) {
              geocoder.geocode({
                  'address': address
              }, function(results, status) {
                  if (status == google.maps.GeocoderStatus.OK) {
                      map.setCenter(results[0].geometry.location);
                      var marker2 = new google.maps.Marker({
                          map: map,
                          icon: homeimage,
                          title: address,
                          position: results[0].geometry.location
                      });
                  } else {
                      alert("Geocode was not successful for the following reason: " + status);
                  }
              });
          }
      }

