body.script:
        $(document).ready(function () {
            $("#search-header").autocomplete({
                source: function (request, response) {
                    $.ajax({
                        url: "/Api/Suggest",
                        data: {
                            query: request.term,
                            culture: 'en'
                        },
                        success: function (data) {
                            response(data);
                        }
                    });
                },
                minLength: 2
            });

            $(".Link--search").click(function () {
                $("#search-header").focus();
            });
        });
