div.script:
EasyBlog.ready(function($){

	// Bind event's on the font size changer.
	$('[data-font-resize]').on('click', function() {

		// General font size
		var content = $('[data-blog-content]'),
			current = content.css('font-size'),
			num = parseFloat(current, 10),
			unit = current.slice(-2),
			operation = $(this).data('operation');

		// 
 tag size
		var pTag = content.find('p'),
			pNum = parseFloat(pTag.css('font-size'), 10);

		//  size
		var spanTag = content.find('span'),
			spanNum = parseFloat(spanTag.css('font-size'), 10);

		//  header tag
		var header = content.find(':header'),
			headerNum = parseFloat(header.css('font-size'), 10);

		// block link description text
		var linksBlock = $('[data-type="links"]'),
			linksDesc = linksBlock.find('.media-content');

		if (operation == 'increase') {
			num = num * 1.4;
			pNum = pNum * 1.4;
			spanNum = spanNum * 1.4;
			headerNum = headerNum * 1.4;
		}

		if (operation == 'decrease') {
			num = num / 1.4;
			pNum = pNum / 1.4;
			spanNum = spanNum / 1.4;
			headerNum = headerNum / 1.4;
		}

		content.css('font-size', num + unit);
		pTag.css('font-size', pNum + unit);
		spanTag.css('font-size', spanNum + unit);
		header.css('font-size', headerNum + unit);
		linksDesc.css('font-size', num + unit);
	});
});

EasyBlog.ready(function($) {

    $('[data-post-print]').on('click', function(event) {
        var el = $(this),
            url = el.attr('href');


        window.open(url, 'win2', 'status=no,toolbar=no,scrollbars=yes,titlebar=no,menubar=no,resizable=yes,width=640,height=480,directories=no,location=no');

        // Prevent bubbling up.
        event.preventDefault();
    });
});

EasyBlog.require()
.script('site/posts/posts', 'site/posts/readingprogress')
.done(function($) {



	// Implement post library
	$('[data-blog-post]').implement(EasyBlog.Controller.Posts, {
		"ratings": false	});

			$('[data-blog-post]').implement(EasyBlog.Controller.Posts.Readingprogress, {
			"autoload" : false		});
	});

EasyBlog.require()
.script('site/posts/entry', 'https://www.googletagmanager.com/gtag/js?id=')
	
.done(function($) {
	var trackingId = '';

	
	$('[data-eb-posts]').implement(EasyBlog.Controller.Entry, {
		"autoload": true,
		"ga_enabled": false,
		"ga_tracking_id": trackingId,
		"currentPageUrl": "http://www.sspencer.k12.in.us/news/district/free-meals-during-covid-19-school-closure"
	});
});

EasyBlog.ready(function($){

	// Prevent closing
	$(document).on('click.toolbar', '[data-eb-toolbar-dropdown]', function(event) {
		event.stopPropagation();
	});

	// Logout
	$(document).on('click', '[data-blog-toolbar-logout]', function(event) {
		$('[data-blog-logout-form]').submit();
	});

	// Search
	$('[data-eb-toolbar-search]').on('click', function() {
		$('[data-eb-toolbar-search-wrapper]').toggleClass('hide');
	});

	});

body.script:
jQuery(document).ready( function() {
	var viewportWidth = jQuery(window).width();
	if (viewportWidth > 1600) { 
		var iwidth = 400; 
	} else if (viewportWidth > 1400 ) { 
		var iwidth = 350;
	} else if (viewportWidth > 1200 ) { 
		var iwidth = 320;
	} else if (viewportWidth < 1200 && viewportWidth > 960) { 
		var iwidth = 300; 
    } else if (viewportWidth < 960 && viewportWidth > 400) { 
		var iwidth = 280; 
	} else if (viewportWidth < 400 ) { 
		var iwidth = 275; 
	} else  { 
		var iwidth = 275; 
	}

	if(jQuery('#facebook_embed').length > 0){
				document.getElementById("facebook_embed").innerHTML = "";
			document.getElementById("twitter_embed").innerHTML = "Tweets by SSHS_Rebels";
	}

